"use strict";(self.webpackChunkweb=self.webpackChunkweb||[]).push([[866],{4612:r=>{r.exports=JSON.parse('{"blogPosts":[{"id":"\ub370\uc774\ud130 \ud328\uce6d \ucd5c\uc801\ud654\ub97c \ud1b5\ud55c API \ube44\uc6a9 \ucd5c\uc801\ud654","metadata":{"permalink":"/blog/\ub370\uc774\ud130 \ud328\uce6d \ucd5c\uc801\ud654\ub97c \ud1b5\ud55c API \ube44\uc6a9 \ucd5c\uc801\ud654","editUrl":"https://github.com/minsgy/minsgy.github.io/edit/main/blog/react-query-optimization.md","source":"@site/blog/react-query-optimization.md","title":"\ub370\uc774\ud130 \ud328\uce6d \ucd5c\uc801\ud654\ub97c \ud1b5\ud55c API \ube44\uc6a9 \ucd5c\uc801\ud654","description":"\uc11c\ub860","date":"2023-04-08T13:49:08.000Z","formattedDate":"2023\ub144 4\uc6d4 8\uc77c","tags":[{"label":"TypeScript","permalink":"/blog/tags/type-script"},{"label":"React","permalink":"/blog/tags/react"},{"label":"react-query","permalink":"/blog/tags/react-query"},{"label":"optimization","permalink":"/blog/tags/optimization"},{"label":"fetch","permalink":"/blog/tags/fetch"}],"readingTime":9.225,"hasTruncateMarker":false,"authors":[{"name":"Choi Minseok","title":"\uc3d8\uce74(SOCAR)\uc5d0\uc11c \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc744 \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.","url":"https://github.com/minsgy","imageURL":"https://github.com/minsgy.png","key":"minsgy"}],"frontMatter":{"slug":"\ub370\uc774\ud130 \ud328\uce6d \ucd5c\uc801\ud654\ub97c \ud1b5\ud55c API \ube44\uc6a9 \ucd5c\uc801\ud654","title":"\ub370\uc774\ud130 \ud328\uce6d \ucd5c\uc801\ud654\ub97c \ud1b5\ud55c API \ube44\uc6a9 \ucd5c\uc801\ud654","authors":["minsgy"],"tags":["TypeScript","React","react-query","optimization","fetch"]},"nextItem":{"title":"\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30","permalink":"/blog/\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30"}},"content":"## \uc11c\ub860\\r\\n\\r\\n- \ud504\ub85c\uc81d\ud2b8\ub97c \uc9c4\ud589\ud558\uba74\uc11c Google Map API \uc911 \uc704\ub3c4, \uacbd\ub3c4\ub97c \uc8fc\uc18c\ub85c \ubcc0\ud658\ud558\ub294 \ub85c\uc9c1\uc744 \ud544\uc694\ub85c \ud588\uace0 \uc81c\uacf5\ud55c \ud574\ub2f9 API\ub294 \uac74\ub2f9 6.6\uc6d0\uc758 \ube44\uc6a9\uc774 \ubc1c\uc0dd\ud588\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \ub2e4\uc591\ud55c \uc0ac\uc6a9\uc790\uac00 \ub9ce\uc744 \uc218\ub85d \uad49\uc7a5\ud788 \ub9ce\uc740 \ud638\ucd9c\uc774 \uc774\ub8e8\uc5b4\uc9c0\ub2e4\ubcf4\ub2c8 \ube44\uc6a9\uc801\uc778 \ubd80\ub2f4\uc774 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ube44\uc6a9\uc744 \ucd5c\uc801\ud654\ud558\uae30 \uc704\ud574\uc11c\ub294 \ucd5c\ub300\ud55c \uc801\uc740 API \ud638\ucd9c\uc744 \ud574\uc57c \ud588\uc5c8\uace0 \ud574\ub2f9 \uacfc\uc815\uc744 \ud3ec\uc2a4\ud305\ud558\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ubc88 \ud3ec\uc2a4\ud305\uc5d0\uc11c\ub294 API\ub97c \ube44\uc6a9\uc744 \ucd5c\uc801\ud654\ud558\uae30 \uc704\ud55c \ubc29\ubc95\uc744 \uc54c\uc544\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n## \ubb38\uc81c \uc0c1\ud669\\r\\n\\r\\n- \uc11c\ube44\uc2a4\uc5d0\uc11c \ube44\uc6a9\uc774 \ub4dc\ub294 API\ub97c \ud638\ucd9c\ud558\ub294 \uacbd\uc6b0\uac00 \uc788\uc2b5\ub2c8\ub2e4. \uc0ac\uc6a9\uc790\uac00 \ub9ce\uc740 \uc11c\ube44\uc2a4\uc758 \uacbd\uc6b0\ub294 \ube44\uc6a9\uc774 \uae30\ud558\uae09\uc218\uc801\uc73c\ub85c \ub298\uc5b4\ub0a0 \uc218 \uc788\uc744 \ubfd0\ub9cc \uc544\ub2c8\ub77c \ud398\uc774\uc9c0 \uccab \uc811\uc18d\ub54c \ube44\uc6a9\uc774 \ub4dc\ub294 \ub370\uc774\ud130 \ud328\uce6d\uc774 \uc874\uc7ac\ud558\uac8c \ub420 \uacbd\uc6b0 \ube44\uc6a9 \ub0a8\ube44\uac00 \ub354 \uc2ec\ud560 \uc218 \uc788\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4. \ub2e8\uc21c\ud788 \uc0c8\ub85c\uace0\uce68\uc744 \ud1b5\ud574 \ud55c \uba85\uc758 \uc0ac\uc6a9\uc790\uac00 \uc5ec\ub7ec \ubc88 API\ub97c \ud638\ucd9c\ud558\ub294 \uacbd\uc6b0\ub3c4 \uc788\uace0 \uc545\uc758(?)\ub97c \uac16\uace0 \ubb34\ud55c \uc0c8\ub85c\uace0\uce68\uc744 \ud558\uac8c \ub41c\ub2e4\uba74 100\ubc88 \uc774\uc0c1\uc758 API \ud638\ucd9c\uc744 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc989, \ube44\uc6a9\uc774 \ub9ce\uc774 \ub4dc\ub294 API\ub97c \ud638\ucd9c\ud558\ub294 \uacbd\uc6b0\uc5d0\ub294 \ucd5c\ub300\ud55c \uc801\uc740 API \ud638\ucd9c\uc744 \ud558\ub3c4\ub85d \ucd5c\uc801\ud654\ub97c \ud574\uc57c \ud569\ub2c8\ub2e4. \ud604\uc7ac \ubb38\uc81c \uc0c1\ud669\uc744 \uc815\ub9ac\ud558\uba74 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n  - \ube44\uc6a9\uc774 \ub9ce\uc774 \ub4dc\ub294 API\ub97c \ud638\ucd9c\ud558\ub294 \uacbd\uc6b0\\r\\n  - \uc0ac\uc6a9\uc790\uac00 \ub9ce\uc740 \uc11c\ube44\uc2a4\uc758 \uacbd\uc6b0 \ube44\uc6a9\uc774 \uae30\ud558\uae09\uc218\uc801\uc73c\ub85c \ub298\uc5b4\ub0a0 \uc218 \uc788\uc74c.\\r\\n  - \ud398\uc774\uc9c0 \uccab \uc811\uc18d\ub54c \ube44\uc6a9\uc774 \ub4dc\ub294 \ub370\uc774\ud130 \ud328\uce6d\uc774 \uc874\uc7ac\ud558\uac8c \ub420 \uacbd\uc6b0 \ube44\uc6a9 \ub0a8\ube44\uac00 \ub354 \uc2ec\ud560 \uc218 \uc788\uc74c.\\r\\n  - \uc0c8\ub85c\uace0\uce68\uc744 \ud1b5\ud574 \ud55c \uba85\uc758 \uc0ac\uc6a9\uc790\uac00 \uc5ec\ub7ec \ubc88 API\ub97c \ud638\ucd9c\ud558\ub294 \uacbd\uc6b0 (\uc545\uc758\ub97c \uac00\uc9c0\uace0 \ubb34\ud55c \uc0c8\ub85c\uace0\uce68\uc744 \ud558\uac8c \ub41c\ub2e4\uba74 \uc9e7\uc740 \uc2dc\uac04\uc5d0 100\ubc88 \uc774\uc0c1\uc758 API \ud638\ucd9c\uc744 \ud560 \uc218 \uc788\uc74c)\\r\\n  - \ube44\uc6a9\uc774 \ub9ce\uc774 \ub4dc\ub294 API\ub97c \ud638\ucd9c\ud558\ub294 \uacbd\uc6b0\uc5d0\ub294 \ucd5c\ub300\ud55c \uc801\uc740 API \ud638\ucd9c\uc744 \ud558\ub3c4\ub85d \ucd5c\uc801\ud654\ub97c \ud574\uc57c \ud568.\\r\\n\\r\\n**\ubb38\uc81c \ud574\uacb0\uc744 \uc704\ud574 API \ube44\uc6a9\uc744 \ucd5c\uc18c\ud654\ud558\uae30 \uc704\ud55c \ubc29\ubc95\uc744 \uace0\ubbfc\ud588\uace0 `API \ud638\ucd9c \ucd5c\uc18c\ud654\ub97c \uc704\ud55c \ub370\uc774\ud130\ub97c \uce90\uc2f1`\uc73c\ub85c \ubc29\ud5a5\uc131\uc744 \uc7a1\uace0 \uc791\uc5c5\ud588\uc2b5\ub2c8\ub2e4.**\\r\\n\\r\\n## queryKey\ub97c \ud1b5\ud55c \ub370\uc774\ud130 \uce90\uc2f1 \ubc29\ubc95\\r\\n\\r\\n- React \ud658\uacbd\uc5d0\uc11c \ub370\uc774\ud130 \ud328\uce6d \ub3c4\uad6c\ub85c `react-query`\ub97c \ud65c\uc6a9\ud588\uc2b5\ub2c8\ub2e4. useQuery \uba54\uc18c\ub4dc\uc5d0 \uc9c0\uc6d0\ud558\ub294 [queryKey](https://tanstack.com/query/v4/docs/react/guides/query-keys)\ub97c \ud65c\uc6a9\ud558\uc5ec \ub370\uc774\ud130 \uce90\uc2f1\uc744 \uc720\uc6a9\ud558\uac8c \uad00\ub9ac\ud560 \uc218 \uc788\ub294 \uae30\ub2a5\uc744 \ud65c\uc6a9\ud558\ub824\uace0 \ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```jsx\\r\\n    useQuery([\'minsgy\', { status, page }], ...)\\r\\n    useQuery([\'minsgy\', { page, status }], ...)\\r\\n    useQuery([\'minsgy\', { page, status, other: undefined }], ...)\\r\\n```\\r\\n\\r\\n\uc704\uc640 \uac19\uc740 \ubc29\uc2dd\uc744 \ud1b5\ud574 \ud398\uc774\uc9c0 \uc811\uc18d \uc2dc API \ud638\ucd9c\uc740 \ucd5c\uc18c\ud654\ud560 \uc218 \uc788\uc5c8\uace0 \ubd88\ud544\uc694\ud55c API \ud638\ucd9c\uc740 \ub9c9\uc744 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uadf8\ub7ec\ub098 **queryKey\ub85c\ub9cc \uc6d0\ud558\ub294 \ubc29\uc2dd\uc758 \ub370\uc774\ud130 \uce90\uc2f1 \uad00\ub9ac\ub294 \ubd88\uac00\ub2a5\ud588\uc2b5\ub2c8\ub2e4.** \uc0c8\ub85c\uace0\uce68\uc744 \ud1b5\ud558\uba74 \ud074\ub77c\uc774\uc5b8\ud2b8 \uc0c1\ud0dc\uac00 \ucd08\uae30\ud654\ub418\uc5b4 \uc0c8\ub85c\uc6b4 API \ud638\ucd9c\uc774 \uc774\ub8e8\uc5b4\uc9c0\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n## Storage\ub97c \ud1b5\ud55c \ub370\uc774\ud130 \uce90\uc2f1 \ubc29\ubc95\\r\\n\\r\\n\ud074\ub77c\uc774\uc5b8\ud2b8 \uc0c1\ud0dc\ub97c \uc720\uc9c0\ud574\uc57c \ud588\uace0 Fetching\ud55c \ub370\uc774\ud130\ub97c \uc800\uc7a5\ud558\ub294 \ubc29\ubc95\uc744 \uace0\ubbfc\ud574 \ucd94\uac00 \ubc29\uc548\uc740 \ub2e4\uc74c\uacfc \uac19\uc558\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n    - Refresh\ub97c \ud1b5\ud574 \ud074\ub77c\uc774\uc5b8\ud2b8 \uc0c1\ud0dc\ub97c \uc720\uc9c0\ud558\uc5ec \ucd94\uac00\uc801\uc778 API \ud638\ucd9c\uc744 \ub9c9\ub294\ub2e4.\\r\\n    - Fetching\ud55c \uc720\ud6a8\ud55c \ub370\uc774\ud130\ub97c \uc800\uc7a5\ud558\uace0 \ucd94\uac00\uc801\uc778 API \ud638\ucd9c\uc744 \ub9c9\ub294\ub2e4.\\r\\n    - +) 5\ubd84 \uc774\uc0c1 \uc9c0\ub09c \ub370\uc774\ud130\ub294 \uc0ad\uc81c \ud6c4, \uc0c8\ub85c\uc6b4 API \ud638\ucd9c\uc744 \ud55c\ub2e4.\\r\\n\\r\\n\ud398\uc774\uc9c0 \ub2e8\uc5d0\uc11c Refresh\uac00 \ubc1c\uc0dd\ud574\ub3c4 \ub370\uc774\ud130\ub97c \uc720\uc9c0\ud560 \uc218 \uc788\ub294 \ubc29\ubc95\uc73c\ub85c web storage\uc640 indexed DB\ub97c \uace0\ubbfc\ud588\uc9c0\ub9cc 100\uc904 \uc774\ud558\uc758 \uc791\uc740 \ub370\uc774\ud130\ub97c \uc800\uc7a5\ud558\ub294 \uac83\uc774 \ubaa9\uc801\uc774\uc5ec\uc11c **WebStorage**\ub97c \ud65c\uc6a9\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### queryKey + timestamp + fetching response data\\r\\n\\r\\n\uc720\ud6a8\ud55c \ub370\uc774\ud130\uc778\uc9c0 \ud655\uc778\ud558\uae30 \uc704\ud55c `timestamp`, `react-query`\uc640 \ud568\uaed8 \uc0ac\uc6a9\ud558\uae30 \uc704\ud55c `queryKey`, \ub370\uc774\ud130 \ud328\uce6d\ud55c response\ub97c \uc800\uc7a5\ud558\uae30 \uc704\ud55c `data`\ub85c \ubb36\uc5b4\uc11c \uad00\ub9ac\ud558\ub294 \ubc29\uc2dd\uc73c\ub85c \uad6c\ud604\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n- \uc544\ub798\uc640 \uac19\uc740 `useCustomCacheQuery` \ucee4\uc2a4\ud140 \ud6c5\uc744 \ub9cc\ub4e4\uc5b4\uc11c timestamp\ub97c \ud1b5\ud574 \uc720\ud6a8\ud55c \ub370\uc774\ud130\uc778\uc9c0 \ud655\uc778\ud558\uace0 \uc720\ud6a8\ud55c \ub370\uc774\ud130\uac00 \uc544\ub2cc \uacbd\uc6b0 API \ud638\ucd9c\uc744 \ud558\ub3c4\ub85d \uad6c\ud604\ud588\uc2b5\ub2c8\ub2e4.\\r\\n- \ucd94\uac00\uc801\uc73c\ub85c `initialData` react-query option\ub97c \ud1b5\ud574 \uce90\uc2f1\ub41c \ub370\uc774\ud130\uac00 \uc788\uc73c\uba74 \uce90\uc2f1\ub41c \ub370\uc774\ud130\ub97c \uc0ac\uc6a9\ud558\ub3c4\ub85d \uad6c\ud604\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```jsx\\r\\n // useCustomCacheQuery.ts\\r\\n  const storedData = JSON.parse(sessionStorage.getItem(queryKey));\\r\\n  const timestamp = storedData ? storedData.timestamp : Date.now();\\r\\n  const queryKey = storedData ? storedData.queryKey : customQueryKey;\\r\\n\\r\\n // \uc800\uc7a5\ub41c \ub370\uc774\ud130\uac00 \uc788\uace0, timestamp\uac00 5\ubd84 \uc774\ub0b4\uc778 \uacbd\uc6b0\\r\\n  if (storedData && timestamp && Date.now() - timestamp < 5 * 60 * 1000) {\\r\\n    ...\\r\\n  }\\r\\n\\r\\n  // \uc800\uc7a5\ub41c \ub370\uc774\ud130\uac00 \uc788\uc9c0\ub9cc, timestamp\uac00 5\ubd84 \uc774\uc0c1\uc778 \uacbd\uc6b0\\r\\n  if (storedData && timestamp && Date.now() - timestamp >= 5 * 60 * 1000) {\\r\\n    ...\\r\\n  }\\r\\n\\r\\n  // \uc800\uc7a5\ub41c \ub370\uc774\ud130\uac00 \uc5c6\uac70\ub098, timestamp\uac00 5\ubd84 \uc774\uc0c1\uc778 \uacbd\uc6b0\\r\\n  if (!storedData || !timestamp || Date.now() - timestamp >= 5 * 60 * 1000) {\\r\\n    ...\\r\\n  }\\r\\n\\r\\n  return useQuery(queryKey, fetchFunction(), {\\r\\n    initialData: storedData ? storedData.data : undefined, // \uce90\uc2f1\ub41c \ub370\uc774\ud130\uac00 \uc788\uc73c\uba74 \uce90\uc2f1\ub41c \ub370\uc774\ud130\ub97c \uc0ac\uc6a9\\r\\n    ...\\r\\n  })\\r\\n```\\r\\n\\r\\n### \ub098\uc62c \uc218 \uc788\ub294 \ubb38\uc81c\uc810\uacfc \uace0\ubbfc \ud574\uc57c \ud560 \uc810\\r\\n\\r\\n\uc774\ub7ec\ud55c \uce90\uc2f1 \ub370\uc774\ud130\ub97c \uc0ac\uc6a9\ud560 \ub54c \ub098\uc62c \uc218 \uc788\ub294 \ubb38\uc81c\uc810\uc740 \ub2e4\uc74c\uacfc \uac19\uc558\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n    - \uc0ac\uc6a9\uc790\uac00 \ud655\uc778\ud558\uace0 \uc788\ub294 \ub370\uc774\ud130\uac00 \uc2e0\ub8b0\ud560 \uc218 \uc788\ub294 \ub370\uc774\ud130\uc778\uc9c0 \ud655\uc778\ud558\uae30 \uc5b4\ub835\ub2e4.\\r\\n    - \uce90\uc2f1\ub41c \ub370\uc774\ud130\uac00 \uc2e4\uc81c \ub370\uc774\ud130\uc640 \ub2e4\ub97c \uc218 \uc788\ub2e4.\\r\\n\\r\\n\ud574\ub2f9 \ub85c\uc9c1\uc744 \uc0ac\uc6a9\ud558\uae30 \uc704\ud574\uc11c\ub294 \uc11c\ube44\uc2a4 \ud2b9\uc9d5\uc744 \uace0\ubbfc\ud558\uace0 \uc801\uc808\ud55c \ubc29\ubc95\uc744 \uc120\ud0dd\ud574\uc57c \ud569\ub2c8\ub2e4.\\r\\n\\r\\n    - \uce90\uc2f1 \ub41c \ub370\uc774\ud130\ub97c \uc81c\uacf5\ud560 \ub54c \ubb38\uc81c\uac00 \ub420 \uc218 \uc788\ub294\uac00?\\r\\n    - \ub370\uc774\ud130 \uc815\ud569\uc131\uc774 \uc911\uc694\ud55c\uac00? (\ub370\uc774\ud130\uac00 \uc2e4\uc2dc\uac04\uc73c\ub85c \ubcc0\uacbd\ub418\ub294 \uacbd\uc6b0)\\r\\n    - \ub370\uc774\ud130 \uc815\ud569\uc131\uc774 \uc911\uc694\ud558\uc9c0 \uc54a\uc740\uac00? (\ub370\uc774\ud130\uac00 \uc2e4\uc2dc\uac04\uc73c\ub85c \ubcc0\uacbd\ub418\uc9c0 \uc54a\ub294 \uacbd\uc6b0)\\r\\n\\r\\n\uc5d0 \ub300\ud55c \uc11c\ube44\uc2a4 \ud2b9\uc9d5\uc744 \uace0\ub824\ud558\uc5ec \uc801\uc808\ud55c \ubc29\ubc95\uc744 \uc120\ud0dd\ud574\uc57c \ud55c\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\r\\n\\r\\n## \uacb0\ub860\\r\\n\\r\\nAPI \ube44\uc6a9\uc744 \ucd5c\uc18c\ud654\ud558\uae30 \uc704\ud574 \ub370\uc774\ud130 \uce90\uc2f1\uc744 \uc0ac\uc6a9\ud558\ub294 \ubc29\ubc95\uc5d0 \ub300\ud574 \uace0\ubbfc\ud574\ubcf4\uc558\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n    - queryKey\ub97c \ud1b5\ud55c \ub370\uc774\ud130 \uce90\uc2f1 \ubc29\ubc95\\r\\n    - Storage\ub97c \ud1b5\ud574 \uc720\ud6a8\ud55c \ub370\uc774\ud130 \uce90\uc2f1\uc744 \uc704\ud55c \ubc29\ubc95\\r\\n\\r\\n\uc704\uc758 \ub450 \uac00\uc9c0 \ubc29\ubc95\uc744 \ud1b5\ud574 API \ube44\uc6a9\uc744 \ucd5c\uc18c\ud654\ud560 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\ud558\uc9c0\ub9cc **queryKey\ub85c\ub9cc \uc6d0\ud558\ub294 \ubc29\uc2dd\uc758 \ub370\uc774\ud130 \uce90\uc2f1 \uad00\ub9ac\ub294 \ubd88\uac00\ub2a5**\ud588\uace0 **Storage\ub97c \ud1b5\ud574 \uc720\ud6a8\ud55c \ub370\uc774\ud130 \uce90\uc2f1\uc744 \uc704\ud55c \ubc29\ubc95**\uc744 \uc0ac\uc6a9\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\ub610\ud55c, **\uce90\uc2f1\ub41c \ub370\uc774\ud130\uac00 \uc2e4\uc81c \ub370\uc774\ud130\uc640 \ub2e4\ub97c \uc218 \uc788\ub2e4.** \ub77c\ub294 \ubb38\uc81c\uc810\uc774 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### react-query\uc5d0\uc11c \uc81c\uacf5\ud558\ub294 persistance \uae30\ub2a5\\r\\n\\r\\n- react-query\uc5d0\uc11c\ub294 [persistQueryClient](https://tanstack.com/query/v4/docs/react/plugins/persistQueryClient) \uae30\ub2a5\uc744 \uc81c\uacf5\ud569\ub2c8\ub2e4.\\r\\n- \uc9c1\uc811 `useCustomCacheQuery` \ucee4\uc2a4\ud140 \ud6c5\uc744 \ub9cc\ub4e4\uc9c0 \uc54a\uace0\ub3c4 \uce90\uc2f1\ub41c \ub370\uc774\ud130\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n- \uc544\uc9c1 `react-query v4`\uc5d0\uc11c\ub294 `persistQueryClient` \uae30\ub2a5\uc774 stable \ubc84\uc804\uc774 \uc544\ub2c8\uae30 \ub54c\ubb38\uc5d0 \uc0ac\uc6a9\uc5d0 \uc8fc\uc758\uac00 \ud544\uc694\ud569\ub2c8\ub2e4."},{"id":"\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30","metadata":{"permalink":"/blog/\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30","editUrl":"https://github.com/minsgy/minsgy.github.io/edit/main/blog/javascript-functional.md","source":"@site/blog/javascript-functional.md","title":"\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30","description":"\uc11c\ub860","date":"2022-10-10T08:34:47.000Z","formattedDate":"2022\ub144 10\uc6d4 10\uc77c","tags":[{"label":"JavaScript","permalink":"/blog/tags/java-script"},{"label":"DeepDive","permalink":"/blog/tags/deep-dive"},{"label":"Study","permalink":"/blog/tags/study"}],"readingTime":8.14,"hasTruncateMarker":false,"authors":[{"name":"Choi Minseok","title":"\uc3d8\uce74(SOCAR)\uc5d0\uc11c \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc744 \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.","url":"https://github.com/minsgy","imageURL":"https://github.com/minsgy.png","key":"minsgy"}],"frontMatter":{"slug":"\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30","title":"\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30","authors":["minsgy"],"tags":["JavaScript","DeepDive","Study"]},"prevItem":{"title":"\ub370\uc774\ud130 \ud328\uce6d \ucd5c\uc801\ud654\ub97c \ud1b5\ud55c API \ube44\uc6a9 \ucd5c\uc801\ud654","permalink":"/blog/\ub370\uc774\ud130 \ud328\uce6d \ucd5c\uc801\ud654\ub97c \ud1b5\ud55c API \ube44\uc6a9 \ucd5c\uc801\ud654"},"nextItem":{"title":"Prototype\uc5d0 \ub300\ud558\uc5ec","permalink":"/blog/Prototype\uc5d0 \ub300\ud558\uc5ec"}},"content":"## \uc11c\ub860\\r\\n\\r\\n\ud504\ub85c\uadf8\ub798\ubc0d \ud328\ub7ec\ub2e4\uc784 \uc911 \ud558\ub098\uc778 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc740 \ubc29\ub300\ud55c \ub370\uc774\ud130\ub4e4\uc744 \ud6a8\uc728\uc801\uc73c\ub85c \uc0ac\uc6a9\ud558\uae30 \uc704\ud574 \ub3c4\uc785\ud55c \ud558\ub098\uc758 \ubc29\ubc95\uc785\ub2c8\ub2e4. \ubcc0\uacbd\uac00\ub2a5\ud55c \uc0c1\ud0dc\ub97c \ucd5c\ub300\ud55c \uc81c\uac70\ud558\ub824\uace0 \ub178\ub825\ud55c \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\ub77c\uace0 \ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \uae00\uc744 \ud1b5\ud574 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0 \ub300\ud574 \uc774\ud574\ud558\uace0 JavaScript\uc5d0\uc11c \ud65c\uc6a9\ud560 \uc218 \uc788\ub294 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \ubc29\ubc95\uc774 \ubb34\uc5c7\uc778 \uc9c0 \uc54c\uc544 \uc5ec\ub7ec \uc2a4\ub808\ub4dc\uc5d0\uc11c \ubb38\uc81c \uc5c6\uc774 \ub3d9\uc791\ud558\ub294 \ud504\ub85c\uadf8\ub7a8\uc744 \uc27d\uac8c \uc791\uc131\ud574\ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\\r\\n\\r\\n## \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uacfc \uac1d\uccb4\uc9c0\ud5a5 \ud504\ub85c\uadf8\ub798\ubc0d\\r\\n\\r\\n![image](https://velog.velcdn.com/images%2Fminsgy%2Fpost%2Ffcaf675c-18f0-48d9-9d13-21d1375e3bbb%2Fimage.png)\\r\\n\\r\\n\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uacfc \uac1d\uccb4\uc9c0\ud5a5 \ud504\ub85c\uadf8\ub798\ubc0d\uc740 \uc790\uc8fc \ube44\uad50\ub418\uc5b4 \ub098\uc624\ub294 \ud328\ub7ec\ub2e4\uc784 \uac1c\ub150\uc785\ub2c8\ub2e4.\\r\\n\\r\\n\ub450 \uac00\uc9c0 \ud328\ub7ec\ub2e4\uc784 \ub2e4 \ubaa8\ub450 \uc720\ud6a8\ud55c \ud328\ub7ec\ub2e4\uc784\uc774\uace0 \ub9ce\uc774 \uc0ac\uc6a9\ud558\uace0 \uc788\ub294 \ud504\ub85c\uadf8\ub798\ubc0d \ubc29\ubc95\uc785\ub2c8\ub2e4. \uc774 \ub450\uac00\uc9c0\ub97c \ube44\uad50\ud558\ub294 \uac83\ubcf4\ub2e4\ub294 \\"\uc5b4\ub5a0\ud55c \uc0c1\ud669\uc5d0\uc11c \ub9ce\uc774 \uc0ac\uc6a9\ud574\uc57c \ud560 \uc9c0\\"\uc5d0 \ub300\ud55c \uace0\ubbfc\uc744 \ud1b5\ud574 \uac1c\ubc1c\uc790\ub294 \uace0\ubbfc\ud558\uace0 \uc815\ud574\uc11c \uc815\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \\r\\n\\r\\n### \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc758 \uc7a5/\ub2e8\uc810\\r\\n\\r\\n![image](https://miro.medium.com/max/1100/1*Ws70qz7qdNEc-ajxo821Lg.jpeg)\\r\\n\\r\\n\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc758 \uc7a5\uc810\uc73c\ub85c **\ud6a8\uc728\uc131\uacfc \uc9c0\uc5f0 \ud3c9\uac00(Lazy Evaluation**), \uc911\ucca9 \ud568\uc218(\uace0\ucc28 \ud568\uc218), \uc21c\uc218 \ud568\uc218\ub97c \ud1b5\ud55c \ubc84\uadf8 \uc5c6\ub294 \ucf54\ub4dc, \ubcd1\ub82c \ud504\ub85c\uadf8\ub798\ubc0d**\uacfc \uac19\uc740 \ub2e4\uc591\ud55c \uc774\uc810\uc744 \uc81c\uacf5\ud569\ub2c8\ub2e4. \\r\\n\\r\\n\ud574\ub2f9\ud558\ub294 \ud568\uc218\ub4e4\uc740 \uc5b8\uc81c\ub098 \uc27d\uac8c \ud638\ucd9c\ud558\uace0 \uc7ac\uc0ac\uc6a9\ud560 \uc218 \uc788\ub2e4\ub294 \uc7a5\uc810\uc774 \uc788\uace0 \uba85\ub839\ud615 \ubc29\uc2dd\uc758 \ubc18\ubcf5 \uc791\uc131\uc744 \ud544\uc694\ub85c \ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0 **\ucf54\ub4dc \uc790\uccb4\uac00 \ubb34\uc5c7\uc744(What) \uc5d0 \ucd08\uc810\uc744 \ub9de\ucdb0 \uac1c\ubc1c\ud558\uae30 \ub54c\ubb38\uc5d0 \uacfc\uc815\uc5d0 \ub300\ud55c \uc774\ud574\uac00 \ud544\uc694 \uc5c6\ub2e4\ub294 \uc7a5\uc810**\uc774 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n![image](https://thecodebytes.com/wp-content/webpc-passthru.php?src=https://thecodebytes.com/wp-content/uploads/2022/03/functional-programming-meme-8.jpeg&nocache=1)\\r\\n\\r\\n\uc704\uc640 \uac19\uc774 \uc88b\uc740 \uc7a5\uc810\ub4e4\uc774 \uc788\uc9c0\ub9cc \uac00\uc7a5 \ud070 \ubb38\uc81c\uc778 **\ud5c8\ub4e4\uc774 \ub192\ub2e4\ub294 \uc810**\uc785\ub2c8\ub2e4. (\ud568\uc218\uc758 \ud568\uc218\uc758.....?!) \\r\\n\\r\\n\uae30\ub2a5\uc801\uc778 \uad00\uc810\uc5d0\uc11c \ucf54\ub4dc\ub97c \uc811\uadfc\ud558\uae30 \uc704\ud574\uc11c \ubaa8\ub4c8\ud654\ub97c \uc9c4\ud589\ud558\uac8c \ub418\ub294\ub370 \uc774\ub97c \uac1d\uccb4 \uc9c0\ud5a5 \uc6a9\uc5b4\ub85c \uc774\ud574\ud558\uae30 \uc27d\uc9c0\ub9cc, \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc758 \uc815\uc758\ub294 **\\"\ub370\uc774\ud130 \uc870\uc791\\"** \uc5d0 \ucd08\uc810\uc774 \ub9de\ucdb0\uc838 \uc788\uc2b5\ub2c8\ub2e4. \uc7ac\uc0ac\uc6a9\uc131\uacfc \uc21c\uc218 \ud568\uc218\ub85c\uc11c\uc758 \uc5ed\ud560\uc744 \uad6c\uc131\ud558\uae30\ub294 \uc27d\uc9c0 \uc54a\uace0 \uc7ac\uadc0(Recursion), \ubaa8\ub098\ub4dc(Monad), \ud391\ud130(Functor), \ucee4\ub9c1(curry) \ub9ce\uc740 \ud568\uc218\ud615 \uac1c\ub150\uc744 \uc774\ud574\ud574\uc57c \ud569\ub2c8\ub2e4. \uadf8\ub798\uc11c \uc885\ud569\uc801\uc73c\ub85c **\uacbd\ud5d8\uacfc \ud559\uc2b5\uc774 \ub9ce\uc774 \ud544\uc694\ud55c \ud328\ub7ec\ub2e4\uc784**\uc785\ub2c8\ub2e4.\\r\\n\\r\\n\\r\\n### \ub450 \uac00\uc9c0\ub97c \ube44\uad50\ud574\uc57c \ud560\uae4c?\\r\\n\\r\\n\\r\\n![image](https://res.cloudinary.com/practicaldev/image/fetch/s--NDmyycvN--/c_limit%2Cf_auto%2Cfl_progressive%2Cq_auto%2Cw_880/https://thepracticaldev.s3.amazonaws.com/i/bryqu84a8mqrlf3bbvta.jpg)\\r\\n\\r\\n\uc0ac\uc2e4 \ub450 \ud328\ub7ec\ub2e4\uc784\uc744 \ube44\uad50\ud558\uae30\uc5d0\ub294 \uace0\uc720\ud55c \ubb38\uc81c\ub4e4\uc774 \uc788\uc2b5\ub2c8\ub2e4. \\r\\n\\r\\n\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc758 \uacbd\uc6b0, \ubcf5\uc7a1\ud55c \ub85c\uc9c1\uc744 \uad6c\uc0c1\ud558\uac8c \ub418\uba74 \ub108\ubb34 \ub9ce\uc740 \uc885\uc18d\uc131 \uc8fc\uc785 \ub41c \ucf54\ub4dc\ub4e4\uc774 \uc874\uc7ac\ud558\uba70, OOP\uc5d0\ub294 \ub108\ubb34 \ub9ce\uc740 \ub808\uc774\uc5b4\ub97c \ud1b5\ud55c \ucf54\ub4dc\uac00 \ub098\uc624\uac8c \ub429\ub2c8\ub2e4. \\r\\n\\r\\n\uacb0\uad6d **\uac01 \uc7a5\ub2e8\uc810\uc744 \uace0\ub824\ud574\uc11c \uc8fc\uc785\ud574\uc57c \ud558\ub294 \uac1c\ubc1c\uc790\uc758 \ubaab**\uc774\ub77c\uace0\ub3c4 \uc0dd\uac01\uc774 \ub429\ub2c8\ub2e4. \\r\\n\\r\\n\\r\\n\\r\\n## \ud568\uc218\ud615 \ubc29\uc2dd\uc758 JavaScript\\r\\n\\r\\n\uc0ac\uc2e4 JavaScript\ub97c \ud1b5\ud55c \uc644\ubcbd\ud55c \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc740 \uc5b4\ub824\uc6b8 \uc218 \uc788\uc9c0\ub9cc \uc804\ubc18\uc801\uc778 \uac1c\ub150\ub4e4\uc744 \uc815\uc758\ud558\uc5ec \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// map, reduce, filter\ub294 \ud568\uc218\ud615\uc5d0 \uc18d\ud55c\ub2e4.\\r\\n// \uae30\uc874 \ubcc0\uc218\uc5d0 \ub300\ud55c \uc0ac\uc774\ub4dc \uc774\ud399\ud2b8\ub85c \uc5c6\ub3c4\ub85d \uad6c\ud604\ud558\ub294 \uac83\uc774 \uc6d0\uce59.\\r\\n\\r\\n// map\\r\\nconst arr = [1,2,3,4,5];\\r\\nconst map = arr.map(function(x) { \\r\\n return x * 2;\\r\\n}); // [2,4,6,8,10]\\r\\n\\r\\n// filter\\r\\nconst arr = [4, 15, 377, 395, 400, 1024, 3000];\\r\\nconst arr2 = arr.filter((v) => (v % 5 === 0));\\r\\nconsole.log(arr2) // [15, 395, 400, 3000]\\r\\n\\r\\n// reduce\\r\\nlet arr = [9, 2, 8, 5, 7];\\r\\nlet sum = arr.reduce((pre, val) => pre + val);\\r\\nconsole.log(sum) // 31\\r\\n```\\r\\n\\r\\nJavaScript\uc5d0\uc11c \ud568\uc218\ud615 \uac1c\ub150\ub4e4\uc744 \uc5b4\ub5bb\uac8c \ub2e4\ub8e8\ub294 \uc9c0 \uc815\uc758 \ud574\ubd05\ub2c8\ub2e4.\\r\\n\\r\\n\\r\\n### \ud3c9\uac00\\r\\n\\r\\n\ud3c9\uac00\ub294 \ucf54\ub4dc\uac00 **\uacc4\uc0b0(Evalution)** \ub418\uc5b4 \uac12\uc744 \ub9cc\ub4dc\ub294 \uac83\uc744 \ub9d0\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\\r\\n```js\\r\\n1 + 2 = 3\\r\\n(1 + 2) + 4 = 7\\r\\n[1, 2+3] = [1, 5]\\r\\n[1, 2, [3, 4]] = [1, 2, Array(2)]\\r\\n```\\r\\n\\r\\n### \uc77c\uae09 \uac1d\uccb4(\uc77c\uae09)\\r\\n\\r\\n\uac12, \ubcc0\uc218, \ud568\uc218 \ud504\ub85c\ud37c\ud2f0, \ud568\uc218 \uacb0\uacfc\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc74c\uc744 \ub9d0\ud569\ub2c8\ub2e4. \ud568\uc218\uac00 \uac12\uc73c\ub85c\ub3c4 \ub2e4\ub8e8\uc5b4\uc9c8 \uc218 \uc788\uc73c\uba70 **\uc870\ud569\uc131\uacfc \ucd94\uc0c1\ud654 \ub3c4\uad6c\ub85c\uc11c \uae30\ubcf8\uc801\uc778 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc758 \ud2c0**\uc774\ub77c \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// \uc77c\uae09\\r\\nconst value = 10;\\r\\nconst add_value_10 = a => a + 10;\\r\\nconst result = add_value_10(value);\\r\\nconsole.log(result); // 20;\\r\\n\\r\\n// \uc77c\uae09 \uac1d\uccb4\\r\\nconst add_5 = a => a + 5;\\r\\nconsole.log(add_5); // a => a + 5;\\r\\nconsole.log(add_5(5)); // 10\\r\\n\\r\\nconst func_1 = () => () => 1;\\r\\nconsole.log(func_1()); // () => 1\\r\\n\\r\\nconst func_2 = func_1();\\r\\nconsole.log(func_2); // () => 1\\r\\nconsole.log(func_2()); // 1\\r\\n```\\r\\n\\r\\n\\r\\n\\r\\n### \uace0\ucc28 \ud568\uc218\uc640 \ud569\uc131 \ud568\uc218\\r\\n\\r\\n\ud568\uc218\ub97c \uac12\uc73c\ub85c \ub2e4\ub8e8\ub294 \ud568\uc218, \ud568\uc218\uc758 \ubc18\ud658 \uac12\uc73c\ub85c \ud568\uc218\ub97c \uc0ac\uc6a9(Closer)\ud558\ub294 \uac78 \ub9d0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \\r\\nReact\uc5d0\uc11c \uc0ac\uc6a9\ud558\ub294 \uace0\ucc28 \ucef4\ud3ec\ub10c\ud2b8(HOC)\uac00 \uc774\uc5d0 \ud574\ub2f9 \ub429\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// \ud568\uc218 \ub85c\uc9c1\uc744 \ubcf4\ub0b4\uc11c \uac1c\uc870\\r\\nconst higher-order = func => func(1);\\r\\nconst add_2 = a => a + 2;\\r\\nconsole.log(higher-order(add_2)); // 3\\r\\nconsole.log(higher-order(a => a - 1)); // 0\\r\\n\\r\\n// Closer + \ud569\uc131 \ud568\uc218\\r\\nconst add_func = a => b => a + b;\\r\\nconst add_10 = add_func(10);\\r\\nconsole.log(add_10(5)); // 15\\r\\nconsole.log(add_10(10)); // 20\\r\\nconsole.log(add_function(10)(5)); // 15 \\r\\n```\\r\\n\\r\\n\\r\\n\uadf8 \uc678\ub85c\ub3c4 \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc758 \uac1c\ub150\ub4e4\uc744 \uc801\uc6a9\ud55c \uc0ac\ub840\ub97c \uc0b4\ud3b4\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### \ubd88\ubcc0\uc131\\r\\n\\r\\n\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc5d0\uc11c\ub294 \ud568\uc218 \uc678\ubd80\uc758 \ub370\uc774\ud130\ub97c \ubcc0\uacbd\ud558\uc9c0 \uc54a\ub3c4\ub85d \ud569\ub2c8\ub2e4. \\r\\n\\r\\n\ub9cc\uc57d \ub370\uc774\ud130 \ubcc0\uacbd\uc774 \ud544\uc218\uc801\uc77c \uacbd\uc6b0, \uc6d0\ubcf8 \ub370\uc774\ud130\ub97c \ubcc0\uacbd\ud558\uc9c0 \uc54a\uace0 \uadf8 \ub370\uc774\ud130\uc758 \ubcf5\uc0ac\ubcf8\uc744 \ub9cc\ub4e4\uc5b4 \ubcc0\uacbd \uc791\uc5c5\uc744 \uc9c4\ud589\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nconst red = { name:\'red\' };\\r\\n\\r\\n// \uc6d0\ubcf8 \ub370\uc774\ud130\ub97c \ubcc0\uacbd\ud558\ub294 \ubc29\ubc95\\r\\nfuncion changeColor(color,name){\\r\\n   color.name = name;\\r\\n   return color;\\r\\n}\\r\\nconsole.log(changeColor(red,\'yellow\')); //{name:\'yellow\'}\\r\\nconsole.log(red); //{name:\'yellow\'}\\r\\n\\r\\n// \ubd88\ubcc0\uc131\uc744 \uc9c0\ud0a4\ub294 \ubc29\ubc95.\\r\\nfunction changeColor(color,name){\\r\\n   return Object.assign({},color,{name}); // \ubcf5\uc0ac\ud574\uc11c \uc0ac\uc6a9\ud558\ub294 slice\ub3c4 \uac00\ub2a5\ud569\ub2c8\ub2e4.\\r\\n}\\r\\n\\r\\nconsole.log(changeColor(red,\'yellow\')); //{name: \'yellow\'}\\r\\nconsole.log(red.name); //{name: \'red\'}\\r\\n```\\r\\n\\r\\n\uadf8 \uc678\ub85c\ub3c4 JavaScript\uc5d0\uc11c \uc9c0\uc6d0\ud558\ub294 \ubd88\ubcc0\uc131\uc744 \uae30\ubc18\ud574 \ub370\uc774\ud130 \ubcf5\uc0ac\ubcf8\uc744 \uc81c\uacf5\ud558\ub294 \uba54\uc18c\ub4dc\ub97c \ud65c\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// map\\r\\nconst arr = [\'foo\', \'hello\', \'diamond\', \'A\'];\\r\\nconst arr2 = arr.map((v) => v.length);\\r\\nconsole.log(arr2) // [3, 5, 6, 1]\\r\\n\\r\\n// filter\\r\\nconst arr = [4, 15, 377, 395, 400, 1024, 3000];\\r\\nconst arr2 = arr.filter((v) => (v % 5 === 0));\\r\\nconsole.log(arr2) // [15, 395, 400, 3000]\\r\\n\\r\\n// reduce\\r\\nlet arr = [9, 2, 8, 5, 7];\\r\\nlet sum = arr.reduce((pre, val) => pre + val);\\r\\nconsole.log(sum)\\t// 31 \\r\\n```\\r\\n\\r\\n\\r\\n## Reference\\r\\n\\r\\n- [Functional vs OPP](https://medium.com/@shaistha24/functional-programming-vs-object-oriented-programming-oop-which-is-better-82172e53a526)\\r\\n- [Functional wiki](https://en.wikipedia.org/wiki/Functional_programming)"},{"id":"Prototype\uc5d0 \ub300\ud558\uc5ec","metadata":{"permalink":"/blog/Prototype\uc5d0 \ub300\ud558\uc5ec","editUrl":"https://github.com/minsgy/minsgy.github.io/edit/main/blog/javascript-prototype.md","source":"@site/blog/javascript-prototype.md","title":"Prototype\uc5d0 \ub300\ud558\uc5ec","description":"\uc11c\ub860","date":"2022-09-30T07:46:19.000Z","formattedDate":"2022\ub144 9\uc6d4 30\uc77c","tags":[{"label":"JavaScript","permalink":"/blog/tags/java-script"},{"label":"DeepDive","permalink":"/blog/tags/deep-dive"},{"label":"Study","permalink":"/blog/tags/study"}],"readingTime":13.31,"hasTruncateMarker":false,"authors":[{"name":"Choi Minseok","title":"\uc3d8\uce74(SOCAR)\uc5d0\uc11c \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc744 \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.","url":"https://github.com/minsgy","imageURL":"https://github.com/minsgy.png","key":"minsgy"}],"frontMatter":{"slug":"Prototype\uc5d0 \ub300\ud558\uc5ec","title":"Prototype\uc5d0 \ub300\ud558\uc5ec","authors":["minsgy"],"tags":["JavaScript","DeepDive","Study"]},"prevItem":{"title":"\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30","permalink":"/blog/\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc815\uc758\ud558\uae30"},"nextItem":{"title":"JavaScript Scope\uc640 Closure","permalink":"/blog/JavaScript Scope\uc640 Closure"}},"content":"## \uc11c\ub860\\r\\n\\r\\nJavaScript\uc5d0\uc11c \uc9c0\uc6d0\ud558\uc9c0 \uc54a\ub294 \uc0c1\uc18d \uac1c\ub150\uc778 \uac1d\uccb4\uc9c0\ud5a5 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uad6c\ud604\ud558\uae30 \uc704\ud574 Prototype\uc744 \uc0ac\uc6a9\ud574\uc57c \ud569\ub2c8\ub2e4. Prototype\uc744 \ud1b5\ud574\uc11c \ub113\uc740 \ud655\uc7a5\uc131\uc744 \uac00\uc9c0\ub294 \uac1d\uccb4\uc9c0\ud5a5 \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \uad6c\ud604\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ubc88 \ud3ec\uc2a4\ud305\uc5d0\uc11c\ub294 Prototype\uc5d0 \ub300\ud574 \uc54c\uc544\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n## Property Attribute\\r\\n\\r\\nJavaScript\uc5d0\uc11c\ub294 \uac1d\uccb4\uc758 \ud504\ub85c\ud37c\ud2f0\uc5d0 \ub300\ud55c \uc18d\uc131\uc744 \uc124\uc815\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\uc544\ub798\uc640 \uac19\uc740 \ub0b4\ubd80 \uc2ac\ub86f\uacfc \ub0b4\ubd80 \uba54\uc18c\ub4dc\ub97c \uac00\uc9c0\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n`[[Configurable]]`: \ud504\ub85c\ud37c\ud2f0\uc758 \uc0ad\uc81c, \ud504\ub85c\ud37c\ud2f0 \uc5b4\ud2b8\ub9ac\ubdf0\ud2b8\uc758 \ubcc0\uacbd\uc744 \ud5c8\uc6a9\ud560\uc9c0 \uc5ec\ubd80\ub97c \ub098\ud0c0\ub0c5\ub2c8\ub2e4.\\r\\n\\r\\n`[[Enumerable]]`: \ud504\ub85c\ud37c\ud2f0\uac00 \uc5f4\uac70 \uac00\ub2a5\ud55c\uc9c0 \uc5ec\ubd80\ub97c \ub098\ud0c0\ub0c5\ub2c8\ub2e4.\\r\\n\\r\\n`[[Writable]]`: \ud504\ub85c\ud37c\ud2f0\uc758 \uac12\uc744 \ubcc0\uacbd\ud560 \uc218 \uc788\ub294\uc9c0 \uc5ec\ubd80\ub97c \ub098\ud0c0\ub0c5\ub2c8\ub2e4. \uac12\uc774 false\uc778 \uacbd\uc6b0, \uc77d\uae30 \uc804\uc6a9 \ud504\ub85c\ud37c\ud2f0\uac00 \ub429\ub2c8\ub2e4.\\r\\n\\r\\n`[[Value]]`: \ud504\ub85c\ud37c\ud2f0\uc758 \uac12\uc785\ub2c8\ub2e4.\\r\\n\\r\\n`[[Prototype]]`: \ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4\uc785\ub2c8\ub2e4.\\r\\n\\r\\n`[[Extensible]]`: \ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4\uc5d0 \ud504\ub85c\ud37c\ud2f0\ub97c \ucd94\uac00\ud560 \uc218 \uc788\ub294\uc9c0 \uc5ec\ubd80\ub97c \ub098\ud0c0\ub0c5\ub2c8\ub2e4.\\r\\n\\r\\n`[[Class]]`: \ud504\ub85c\ud37c\ud2f0\uc758 \ud074\ub798\uc2a4\uc785\ub2c8\ub2e4.\\r\\n\\r\\n`[[PrimitiveValue]]`: \ud504\ub85c\ud37c\ud2f0\uc758 \uc6d0\uc2dc \uac12\uc785\ub2c8\ub2e4.\\r\\n\\r\\n`[[Call]]`: \ud504\ub85c\ud37c\ud2f0\uac00 \ud568\uc218\uc778 \uacbd\uc6b0 \ud638\ucd9c\ud560 \uc218 \uc788\ub294\uc9c0 \uc5ec\ubd80\ub97c \ub098\ud0c0\ub0c5\ub2c8\ub2e4.\\r\\n\\r\\n`[[Construct]]`: \ud504\ub85c\ud37c\ud2f0\uac00 \uc0dd\uc131\uc790 \ud568\uc218\uc778 \uacbd\uc6b0 \ud638\ucd9c\ud560 \uc218 \uc788\ub294\uc9c0 \uc5ec\ubd80\ub97c \ub098\ud0c0\ub0c5\ub2c8\ub2e4.\\r\\n\\r\\n`[[HasInstance]]`: \ud504\ub85c\ud37c\ud2f0\uac00 \uc0dd\uc131\uc790 \ud568\uc218\uc778 \uacbd\uc6b0 \uc778\uc2a4\ud134\uc2a4\ub97c \uc0dd\uc131\ud560 \uc218 \uc788\ub294\uc9c0 \uc5ec\ubd80\ub97c \ub098\ud0c0\ub0c5\ub2c8\ub2e4.\\r\\n\\r\\n### \ub0b4\ubd80 \uc2ac\ub86f(\ud504\ub85c\ud37c\ud2f0 \uc5b4\ud2b8\ub9ac\ubdf0\ud2b8) \ub2e4\ub8e8\uae30\\r\\n\\r\\n\uae30\ubcf8\uc801\uc73c\ub85c \ub0b4\ubd80 \uc2ac\ub86f\uc740 JavaScript \uc5d4\uc9c4 \ub0b4\ubd80\uc5d0 \uc874\uc7ac\ud558\uae30 \ub54c\ubb38\uc5d0 \uc811\uadfc\ud558\uac70\ub098 \ud638\ucd9c\ud560 \uc218 \uc788\ub294 \ubc29\ubc95\uc774 \uc874\uc7ac\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc77c\ubd80 \ub0b4\ubd80 \uc2ac\ub86f\uacfc \ub0b4\ubd80 \uba54\uc18c\ub4dc\uc5d0 \ud55c\ud574\uc11c \ucc38\uc870 \ubc29\ubc95\uc73c\ub85c \uc811\uadfc\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nconst object = {}\\r\\n\\r\\nobject.[[Prototype]] // \\"error\\" \ub0b4\ubd80 \uc2ac\ub86f\uc740 JS \uc5d4\uc9c4 \ub0b4\ubd80 \ub85c\uc9c1\uc73c\ub85c \uc811\uadfc \ubd88\uac00\\r\\nobject.__proto__ // Object.prototype \ucc38\uc870\ud560 \uc218 \uc788\ub294 \ub0b4\ubd80 \uba54\uc18c\ub4dc \uc81c\uacf5\\r\\n```\\r\\n\\r\\n\uc704\uc640 \uac19\uc740 \ubc29\ubc95 \uc678\uc5d0\ub3c4 JavaScript \uc5d4\uc9c4 \ud2b9\uc9d5\uc73c\ub85c **\ud504\ub85c\ud37c\ud2f0\uac00 \uc0dd\uc131 \ub420 \ub54c \uc774\ub7ec\ud55c \ud504\ub85c\ud37c\ud2f0\ub97c \uc0c1\ud0dc \uad00\ub9ac\ud560 \uc218 \uc788\ub294 \ud504\ub85c\ud37c\ud2f0 \uc5b4\ud2b8\ub9ac\ubdf0\ud2b8\uac00 \uae30\ubcf8 \uac12\uc73c\ub85c \uc790\ub3d9 \uc815\uc758**\ub418\uac8c \ub429\ub2c8\ub2e4. `value`, `writable`, `enumerable`, `configurable`\uc640 \uac19\uc740 \uc0c1\ud0dc\ub97c \ud655\uc778\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc989, \ud574\ub2f9\ub418\ub294 \ub0b4\ubd80 \uc2ac\ub86f\uc774 \uc0dd\uc131\ub418\uac8c \ub41c\ub2e4\ub294 \ub73b\uc785\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub7ec\ud55c \ud504\ub85c\ud37c\ud2f0 \uc5b4\ud2b8\ub9ac\ubdf0\ud2b8 \uac12 \uc0c1\ud0dc\ub97c \ud655\uc778\ud558\ub294 \uba54\uc18c\ub4dc\ub294 `getOwnPropertyDescript` \uac00 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nconst minsgy = {\\r\\n  age: 24,\\r\\n};\\r\\nconsole.log(Object.getOwnPropertyDescriptor(minsgy, \\"age\\"));\\r\\n// { value: 24, writable: true, emumerable: true, configurable: true }\\r\\n```\\r\\n\\r\\n\uc774\uc640 \uac19\uc774 \ub0b4\ubd80 \uc2ac\ub86f\uc5d0\uc11c \uc874\uc7ac\ud558\ub294 `[[Prototype]]`\uc744 \ud1b5\ud574 JavaScript \ub3d9\uc791 \uc6d0\ub9ac\ub97c \uc54c\uc544\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n## \ud504\ub85c\ud1a0\ud0c0\uc785 Prototype\\r\\n\\r\\nJavaScript\uc5d0\uc11c\ub294 \ud074\ub798\uc2a4 \uae30\ubc18\uc744 \uc0ac\uc6a9\ud558\uace0 \uc788\ub294 \ub2e4\ub978 \uc5b8\uc5b4(Java, C++)\uc640 \ub2ec\ub9ac **\ud504\ub85c\ud1a0\ud0c0\uc785 \uae30\ubc18\uc758 \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4**\uc785\ub2c8\ub2e4. \ud074\ub798\uc2a4\uc5d0\uc11c \uc0ac\uc6a9\ud558\ub294 \uccb4 \uc9c0\ud5a5\uc758 \uc0c1\uc18d \uac1c\ub150\uc744 \ud504\ub85c\ud1a0\ud0c0\uc785\uc73c\ub85c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### prototype chain\\r\\n\\r\\n```js\\r\\nconst student = {\\r\\n  name: \\"Lee\\",\\r\\n  score: 90,\\r\\n};\\r\\n\\r\\nconsole.log(student.hasOwnProperty(\\"name\\")); // true\\r\\nconsole.log(student.__proto__ === Object.prototype);\\r\\nconsole.dir(student);\\r\\n```\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/193508196-c5b6b2fa-c4e1-46ab-a37f-8781e00731cd.png)\\r\\n\\r\\n`student` \uac1d\uccb4\uc758 \uacbd\uc6b0 `hasOwnProperty` \ud504\ub85c\ud37c\ud2f0\uac00 \uc874\uc7ac\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \uc774\uc720\ub294 `__proto__` \ud504\ub85c\ud37c\ud2f0\uc5d0 \uc758\ud574\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\nstudent \uac1d\uccb4\ub294 `__proto__` \ud504\ub85c\ud37c\ud2f0\ub85c \ubd80\ubaa8 \uac1d\uccb4(prototype \uac1d\uccb4)\uc778 `Object.prototype`\ub97c \uac00\ub9ac\ud0a4\uace0 \uc788\uae30 \ub54c\ubb38\uc5d0 \uac00\ub2a5\ud569\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \ud074\ub798\uc2a4 \uac1d\uccb4 \uc9c0\ud5a5\uc758 \uc0c1\uc18d\uc744 \ud65c\uc6a9\ud560 \uc218 \uc788\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uac19\uc740 \uc774\uc720\ub85c `Array.prototype`\uc744 \uc0c1\uc18d\ubc1b\ub294 \ud568\uc218\ub098 \uac1d\uccb4\ub4e4\uc740 \ub2e4\uc74c\uacfc \uac19\uc740 \uba54\uc18c\ub4dc\ub97c \uc0c1\uc18d\ubc1b\uc544 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub7ec\ud55c \uac1c\ub150\uc744 prototype chain\uc774\ub77c\uace0 \ubd80\ub985\ub2c8\ub2e4.\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/193508729-1439f7d6-e466-450a-83d8-2c57f144b5fb.png)\\r\\n\\r\\n\uc774\ucc98\ub7fc \ubaa8\ub4e0 \uac1d\uccb4\ub294 \ubcf8\uc778\uc758 \ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4\ub97c \uac00\ub9ac\ud0a4\ub294 `[[Prototype]]` \ub0b4\ubd80 \uc2ac\ub86f\uc744 \uac16\uc73c\uba74\uc11c \uc0c1\uc18d\uc744 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \ud568\uc218 \uac1d\uccb4\ub9cc\uc758 `prototype` \ud504\ub85c\ud37c\ud2f0\uac00 \ub530\ub85c \uc874\uc7ac\ud569\ub2c8\ub2e4.\\r\\n\\r\\n### prototype property\\r\\n\\r\\n```js\\r\\nfunction Person(name) {\\r\\n  this.name = name;\\r\\n}\\r\\n\\r\\nconst foo = new Person(\\"Lee\\");\\r\\n\\r\\nconsole.dir(Person); // prototype \ud504\ub85c\ud37c\ud2f0\uac00 \uc788\ub2e4.\\r\\nconsole.dir(foo); // prototype \ud504\ub85c\ud37c\ud2f0\uac00 \uc5c6\ub2e4.\\r\\n```\\r\\n\\r\\n`prototype` \ud504\ub85c\ud37c\ud2f0\ub294 \ud568\uc218 \uac1d\uccb4\ub9cc \uac00\uc9c0\uace0 \uc788\uc73c\uba70, \ud568\uc218 \uac1d\uccb4\uac00 \uc0dd\uc131\uc790\ub85c \uc0ac\uc6a9\ub420 \ub54c \uc774 \ud568\uc218\ub97c \ud1b5\ud574 \uc0dd\uc131 \ub420 **\uac1d\uccb4\uc758 \ubd80\ubaa8 \uc5ed\ud560\uc744 \ud558\ub294 \uac1d\uccb4**\ub97c \uac00\ub9ac\ud0a4\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n### constructor property\\r\\n\\r\\n\ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4\ub294 constructor \ud504\ub85c\ud37c\ud2f0\ub97c \uac16\uac8c\ub429\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \uc790\uc2e0\uc744 \uc0dd\uc131\ud55c \uac1d\uccb4\ub97c \uac00\ub9ac\ud0ac \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nfunction Person(name) {\\r\\n  this.name = name;\\r\\n}\\r\\n\\r\\nconst foo = new Person(\\"Lee\\");\\r\\n\\r\\n// Person() \uc0dd\uc131\uc790 \ud568\uc218\uc5d0 \uc758\ud574 \uc0dd\uc131\ub41c \uac1d\uccb4\ub97c \uc0dd\uc131\ud55c \uac1d\uccb4\ub294 Person() \uc0dd\uc131\uc790 \ud568\uc218\uc774\ub2e4.\\r\\nconsole.log(Person.prototype.constructor === Person);\\r\\n\\r\\n// foo \uac1d\uccb4\ub97c \uc0dd\uc131\ud55c \uac1d\uccb4\ub294 Person() \uc0dd\uc131\uc790 \ud568\uc218\uc774\ub2e4.\\r\\nconsole.log(foo.constructor === Person);\\r\\n\\r\\n// Person() \uc0dd\uc131\uc790 \ud568\uc218\ub97c \uc0dd\uc131\ud55c \uac1d\uccb4\ub294 Function() \uc0dd\uc131\uc790 \ud568\uc218\uc774\ub2e4.\\r\\nconsole.log(Person.constructor === Function);\\r\\n```\\r\\n\\r\\n`Class` \uc758 \uac1c\ub150\uc774 \ub418\ub294 \uac1d\uccb4\ub97c \uac00\ub9ac\ud0a4\uac8c \ub429\ub2c8\ub2e4.\\r\\n\uc608\ub97c \ub4e4\uc5b4 \ud45c\ud604\ud558\uc790\uba74 `\ubd95\uc5b4\ube75 \uc0dd\uc131 \ub41c \uc778\uc2a4\ud134\uc2a4`\ub77c\uba74 **\ubd95\uc5b4\ube75\uc744 \ub9cc\ub4e0 \ud2c0**\uc744 \uac00\ub9ac\ud0ac \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### \ud568\uc218 \ub9ac\ud130\ub7f4 \ubc29\uc2dd\uc744 \ud1b5\ud55c \uc120\uc5b8\\r\\n\\r\\n\ud568\uc218\ub97c \uc815\uc758\ud558\ub294 \ubc29\ubc95\uc740 3\uac00\uc9c0\ub85c **\ud568\uc218 \uc120\uc5b8\uc2dd, \ud568\uc218 \ud45c\ud604\uc2dd, new \uc5f0\uc0b0\uc790\ub97c \ud1b5\ud55c \uc0dd\uc131\uc790 \ud568\uc218**\uac00 \uc874\uc7ac\ud569\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// \ud568\uc218 \ud45c\ud604\uc2dd (\ud568\uc218 \ub9ac\ud130\ub7f4 \ubc29\uc2dd)\\r\\nconst square = function (number) {\\r\\n  return number * number;\\r\\n};\\r\\n\\r\\n// \ud568\uc218 \uc120\uc5b8\uc2dd (\uae30\uba85 \ud568\uc218 \ud45c\ud604\uc2dd\uc73c\ub85c \ubcc0\ud658 + \ud568\uc218 \ub9ac\ud130\ub7f4 \ubc29\uc2dd)\\r\\nconst square2 = function square(number) {\\r\\n  return number * number;\\r\\n};\\r\\n```\\r\\n\\r\\n\ub450 \uac00\uc9c0 \ubc29\ubc95 \ub2e4 \ud568\uc218 \ub9ac\ud130\ub7f4 \ubc29\uc2dd\uc744 \uc0ac\uc6a9\ud558\uace0 \uc774\ub7ec\ud55c \ubc29\uc2dd\uc740 \uc0dd\uc131\uc790 \ud568\uc218(Function)\ub85c \uc0dd\uc131\ud558\ub294 \uac83\uc744 \ub2e8\uc21c\ud654\ud55c\uac70\ub85c \ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc989, 3\uac00\uc9c0 \ubc29\ubc95 \ub2e4 **Function() \uc0dd\uc131\uc790 \ud568\uc218\ub97c \ud1b5\ud574 \ud568\uc218 \uac1d\uccb4\ub97c \uc0dd\uc131**\ud569\ub2c8\ub2e4.\\r\\n\\r\\nnew \uc5f0\uc0b0\uc790\ub97c \ud1b5\ud574 \uc0dd\uc131 \ub41c \ud568\uc218\ub97c \uc0b4\ud3b4\ubcf4\uba74 \uc774\uc640 \uac19\uc774 \ub098\ud0c0\ub0bc \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nfunction Person(name, gender) {\\r\\n  // \ud568\uc218\uc774\uae30 \ub54c\ubb38\uc5d0 prototype\uc744 \uac00\uc9c0\uace0\uc788\uc74c.\\r\\n  this.name = name;\\r\\n  this.gender = gender;\\r\\n  this.sayHello = function () {\\r\\n    console.log(\\"Hi! my name is \\" + this.name);\\r\\n  };\\r\\n}\\r\\n\\r\\nconst foo = new Person(\\"Lee\\", \\"male\\"); // \ud568\uc218\uac00 \uc544\ub2c8\uae30\ub54c\ubb38\uc5d0 \ub0b4\ubd80 \uc2ac\ub86f\ub9cc\\r\\n\\r\\nconsole.dir(Person);\\r\\nconsole.dir(foo);\\r\\n\\r\\nconsole.log(foo.__proto__ === Person.prototype); // \u2460 true\\r\\nconsole.log(Person.prototype.__proto__ === Object.prototype); // \u2461 true\\r\\nconsole.log(Person.prototype.constructor === Person); // \u2462 true\\r\\nconsole.log(Person.__proto__ === Function.prototype); // \u2463 true\\r\\nconsole.log(Function.prototype.__proto__ === Object.prototype); // \u2464 true\\r\\n```\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/193511804-5d52915f-e3c7-4379-8ef7-26c3008a1734.png)\\r\\n(\ucc38\uace0. poieweb - prototype)\\r\\n\\r\\n\uc704\uc640 \uac19\uc774 foo\uc758 \uacbd\uc6b0 \ud568\uc218 \uac1d\uccb4\uac00 \uc544\ub2c8\uae30\ub54c\ubb38\uc5d0 `[[Prototype]]` \ub9cc \uc874\uc7ac\ud558\uac8c \ub418\uace0, Person \ud568\uc218\uc758 \uacbd\uc6b0 \ud568\uc218 \uac1d\uccb4\uc774\uae30 \ub54c\ubb38\uc5d0 `prototype`\uc774 \uc874\uc7ac\ud558\uac8c \ub429\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \uc5b4\ub5a4 \ubc29\uc2dd\uc73c\ub85c \ud568\uc218\ub97c \uc120\uc5b8\ud574\ub3c4 `Object.prototype` \uac1d\uccb4\ub97c \uc0c1\uc18d\ubc1b\uc544 \uc0ac\uc6a9\ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n### \ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4\uc758 \ubcc0\uacbd\\r\\n\\r\\n\ud504\ub85c\ud1a0\ud0c0\uc785\uc758 \ud2b9\uc9d5\uc73c\ub85c \uac1d\uccb4\ub97c \uc0dd\uc131\ud560 \ub54c \ud504\ub85c\ud1a0\ud0c0\uc785\uc740 \uacb0\uc815\ub418\uac8c \ub429\ub2c8\ub2e4. \uacb0\uc815\ub41c \ud504\ub85c\ud1a0\ud0c0\uc785\uc758 \uac1d\uccb4\ub294 \ub2e4\ub978 \uc784\uc758 \uac1d\uccb4\ub85c \ubcc0\uacbd\ud560 \uc218 \uc788\ub294\ub370 \uc774\uac83\uc740 **\ubd80\ubaa8 \uac1d\uccb4\uc778 \ud504\ub85c\ud1a0\ud0c0\uc785\uc744 \ub3d9\uc801\uc73c\ub85c \ubcc0\uacbd\ud560 \uc218 \uc788\ub294 \uac83\uc744 \uc758\ubbf8**\ud558\uac8c \ub429\ub2c8\ub2e4. \uc989, \uac1d\uccb4\uc758 \uc0c1\uc18d\uc744 \uad6c\ud604\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uc8fc\uc758 \ud560 \uc810\uc73c\ub85c\ub294 \ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4\ub97c \ubcc0\uacbd\ud558\uac8c \ub410\uc744 \uc2dc \uac1d\uccb4 \ubcc0\uacbd \uc2dc\uc810\uc5d0 \ub530\ub978 `[[Prototype]]` \ubc14\uc778\ub529 \uc2dc\uc810\uc774 \ub2ec\ub77c\uc9c0\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nfunction Person(name) {\\r\\n  this.name = name;\\r\\n}\\r\\n\\r\\nvar foo = new Person(\\"Lee\\");\\r\\n\\r\\n// \ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4\uc758 \ubcc0\uacbd\\r\\nPerson.prototype = { gender: \\"male\\" };\\r\\n\\r\\nvar bar = new Person(\\"Kim\\");\\r\\n\\r\\nconsole.log(foo.gender); // undefined\\r\\nconsole.log(bar.gender); // \'male\'\\r\\n\\r\\nconsole.log(foo.constructor); // \u2460 Person(name)\\r\\nconsole.log(bar.constructor); // \u2461 Object()\\r\\n```\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/193512830-7aa13027-6eda-4a5a-8d19-48aed92e22f6.png)\\r\\n(\ucc38\uace0. poieweb - prototype)\\r\\n1. constructor \ud504\ub85c\ud37c\ud2f0\ub294 Person() \uc0dd\uc131\uc790 \ud568\uc218\ub97c \uac00\ub9ac\ud0a4\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n2. \ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4 \ubcc0\uacbd \uc774\ud6c4\uc5d0\ub294 Person() \uc0dd\uc131\uc790 \ud568\uc218\uc758 Prototype \ud504\ub85c\ud37c\ud2f0\uac00 \uac00\ub9ac\ud0a4\ub294 \ud504\ub85c\ud1a0\ud0c0\uc785 \uac1d\uccb4\ub97c \uc77c\ubc18 \uac1d\uccb4\ub85c \ubcc0\uacbd\ud558\uac8c \ub418\uba74\uc11c `prototype.constructor` \ud504\ub85c\ud37c\ud2f0\uac00 \uc0ac\ub77c\uc9c0\uac8c \ub429\ub2c8\ub2e4. \uacb0\uad6d` bar.constructor` \uac12\uc740 `Object.prototype.constructor`\uc778 Object() \uc0dd\uc131\uc790 \ud568\uc218\uac00 \ub429\ub2c8\ub2e4.\\r\\n\\r\\n`Person.prototype`\uc740 `Person` \uc0dd\uc131\uc790 \ud568\uc218\ub97c \uac00\ub9ac\ud0a4\uc9c0 \uc54a\uace0 Object.prototype\uc744 \uac00\ub9ac\ud0a4\uac8c \ub418\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud558\uac8c \ub429\ub2c8\ub2e4. \uc758\ub3c4\ud558\uc9c0 \uc54a\uc740 \ud504\ub85c\ud1a0\ud0c0\uc785 \uccb4\uc778\uc774 \uc774\ub8e8\uc5b4\uc838 \uc0c1\uc18d\ud574 **\uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \uba54\uc18c\ub4dc\uc5d0 \ub300\ud55c \uc624\ub958\uac00 \ubc1c\uc0dd\ud560 \uc218 \uc788\ub2e4\ub294 \uc810**\uc744 \uace0\ub824\ud574\uc57c\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\ubcc0\uacbd\ub418\ub294 \uc2dc\uc810\uc5d0 \ub530\ub77c \ubc14\uc778\ub529\ub418\ub294\ub370 \uc774\ub97c \ud1b5\ud574 \ub9cc\ub4e4 \uc218 \uc788\ub294 \ucf54\ub4dc \ub514\uc790\uc778 \ud328\ud134\uc744 \uc18c\uac1c\ud569\ub2c8\ub2e4.\\r\\n\\r\\n### \uc774\ub97c \ud65c\uc6a9\ud55c Prototype \ub514\uc790\uc778 \ud328\ud134\\r\\n\\r\\n\ud504\ub85c\ud1a0\ud0c0\uc785\uc758 \ud2b9\uc9d5\uc778 \uc6d0\ud615\uc774 \ub418\ub294 \uc778\uc2a4\ud134\uc2a4\ub97c \uc0ac\uc6a9\ud574 \uc0c8\ub86d\uac8c \uc0dd\uc131 \ud560 \uac1d\uccb4\uc758 \uc885\ub958\ub97c \uba85\uc2dc\ud574\uc11c **\uc0c8\ub85c\uc6b4 \uac1d\uccb4\uac00 \uc0dd\uc131 \ub420 \uc2dc\uc810\uc5d0 \uc778\uc2a4\ud134\uc2a4 \ud0c0\uc785\uc744 \uacb0\uc815\ud558\ub3c4\ub85d \ud558\ub294 \ub514\uc790\uc778 \ud328\ud134**\uc785\ub2c8\ub2e4.\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/193514052-5afd2bc2-640b-46d8-906f-15971e7f5517.png)\\r\\n(\ucc38\uace0. poieweb - prototype)\\r\\n\\r\\n\uc774\ub807\uac8c \ud074\ub77c\uc774\uc5b8\ud2b8\ub294 Prototype \uc778\ud130\ud398\uc774\uc2a4\ub97c \ub530\ub974\ub294 \ubaa8\ub4e0 \uac1d\uccb4\ub97c \ubcf5\uc0ac\ud574\uc11c \uc778\uc2a4\ud134\uc2a4\ub97c \uc0dd\uc131\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. **\uad6c\ud604 \ud074\ub798\uc2a4\uc5d0 \uc758\uc874\ud558\uc9c0 \uc54a\uace0, \uc368\ub4dc \ud30c\ud2f0\uc5d0 \uc885\uc18d\ub418\uc9c0 \uc54a\ub294 \uacbd\uc6b0 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.**\\r\\n\\r\\n#### \uc608\uc81c\\r\\n\\r\\n```js\\r\\n\\r\\nconst Component = function (x, y, c) {\\r\\n   getPosX()\\r\\n   getPosY()\\r\\n   getColor()\\r\\n   getRadius()\\r\\n   ... // \uc88c\ud45c \uacc4\uc0b0 \ub4f1\\r\\n}\\r\\n\\r\\n// Component \uac1d\uccb4 \uc0c1\uc18d\ud558\uae30\\r\\nconst Circle = function (x, y, c) {\\r\\n\\tComponent.call(this, x, y, c)\\r\\n}\\r\\n\\r\\nCircle.prototype = Object.create(Component.prototype)\\r\\nCircle.prototype.constructor = Circle\\r\\n\\r\\n\\r\\n// \ucd94\uac00\ub85c \ud544\uc694\ud55c \ubcc0\uc218 \ubc0f \ud568\uc218\ub97c \uc120\uc5b8\\r\\nCircle.prototype.radius = 0;\\r\\nCircle.prototype.getRadius = function(){...}\\r\\nCircle.prototype.setRadius = function(r){...}\\r\\n\\r\\n\\r\\n// ---- main ---\\r\\n\\r\\nconst circle = new Circle()\\r\\n\\r\\ncircle.getPosX();\\r\\ncircle.getPosY();\\r\\ncircle.getColor();\\r\\ncircle.getRadius();\\r\\n```\\r\\n\\r\\n#### \uc7a5\uc810\\r\\n\\r\\n- \uad6c\ud604 \ud074\ub798\uc2a4\uc5d0 \uc9c1\uc811 \uc5f0\uacb0\ud558\uc9c0 \uc54a\uace0 \uac1d\uccb4\ub97c \ubcf5\uc0ac\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. (\ucef4\ud3ec\ub10c\ud2b8)\\r\\n- \ud504\ub85c\ud1a0\ud0c0\uc785 \uc0c1\uc18d \ub418\uc5b4 \uc788\uc5b4\uc11c \ubcf5\uc7a1\ud55c \uc624\ube0c\uc81d\ud2b8\ub97c \uc27d\uac8c \uad6c\uc131\ud560 \uc218 \uc788\uace0 \uc911\ubcf5 \ucf54\ub4dc\ub97c \uc81c\uac70\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n- \uadf8 \uc678\ub85c \uac1d\uccb4 \ub2e8\uc704\ub97c \ucef4\ud3ec\ub10c\ud2b8\ucc98\ub7fc \ud65c\uc6a9\ud558\uc5ec \ud655\uc7a5\uc131\ub3c4 \uace0\ub824\ud560 \uc218 \uc788\ub2e4\ub294 \uc810\uc774 \ub9e4\ub825\uc785\ub2c8\ub2e4.\\r\\n\\r\\n#### \ub2e8\uc810\\r\\n\\r\\n\uac00\uc7a5 \ubb38\uc81c\uac00 \ub420 \uc218 \uc788\ub294 \ub2e8\uc810\uc73c\ub85c \uc11c\ub85c \uc758\uc874\ub418\uc5b4 \uc788\ub294 \uad00\uacc4\uc77c \uc2dc, \uc0c1\uc18d\ub418\ub294 \ud504\ub85c\ud1a0\ud0c0\uc785 \ud328\ud134\uc744 \uc801\uc6a9\ud558\uae30\uac00 \uc5b4\ub835\uc2b5\ub2c8\ub2e4. \uc21c\ud658 \ucc38\uc870\uac00 \ubc1c\uc0dd\ud558\uc5ec \ubcf5\uc7a1\ud55c \uac1d\uccb4\ub97c \ubd88\ub7ec \uc62c \uc2dc \uc758\uc874\uc131\uc744 \uac16\uace0 \uc788\ub294 \ud504\ub85c\ud1a0\ud0c0\uc785\uc744 \ud65c\uc6a9\ud55c\ub2e4\uba74 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n## Reference\\r\\n\\r\\n- [mdn prototype](https://developer.mozilla.org/ko/docs/Learn/JavaScript/Objects/Object_prototypes)\\r\\n- [mdn new \uc5f0\uc0b0\uc790](https://developer.mozilla.org/ko/docs/Web/JavaScript/Reference/Operators/new)\\r\\n- \ubaa8\ub358 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8 DeepDive"},{"id":"JavaScript Scope\uc640 Closure","metadata":{"permalink":"/blog/JavaScript Scope\uc640 Closure","editUrl":"https://github.com/minsgy/minsgy.github.io/edit/main/blog/javascript-lifecycle.md","source":"@site/blog/javascript-lifecycle.md","title":"JavaScript Scope\uc640 Closure","description":"\uc11c\ub860","date":"2022-09-25T14:21:21.000Z","formattedDate":"2022\ub144 9\uc6d4 25\uc77c","tags":[{"label":"JavaScript","permalink":"/blog/tags/java-script"},{"label":"DeepDive","permalink":"/blog/tags/deep-dive"},{"label":"Study","permalink":"/blog/tags/study"}],"readingTime":12.745,"hasTruncateMarker":false,"authors":[{"name":"Choi Minseok","title":"\uc3d8\uce74(SOCAR)\uc5d0\uc11c \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc744 \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.","url":"https://github.com/minsgy","imageURL":"https://github.com/minsgy.png","key":"minsgy"}],"frontMatter":{"slug":"JavaScript Scope\uc640 Closure","title":"JavaScript Scope\uc640 Closure","authors":["minsgy"],"tags":["JavaScript","DeepDive","Study"]},"prevItem":{"title":"Prototype\uc5d0 \ub300\ud558\uc5ec","permalink":"/blog/Prototype\uc5d0 \ub300\ud558\uc5ec"},"nextItem":{"title":"JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30","permalink":"/blog/JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30"}},"content":"## \uc11c\ub860\\r\\n\\r\\nJavaScript \uc0dd\uba85\uc8fc\uae30\uc778 **\uc2a4\ucf54\ud504(Scope)\uac00 \uc801\uc6a9\ub418\ub294 \uc774\uc720**\ub97c \ud1b5\ud574 \uc6b0\ub9ac\uac00 \uc5b4\ub5a4 \ucf54\ub4dc\ub97c \uc791\uc131\ud560 \uc218 \uc788\ub294\uc9c0 \uace0\ubbfc\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc804\uc5ed\uc801\uc73c\ub85c \uc0ac\uc6a9 \ud560 \uc218 \uc788\ub294 \uc774\uc720\uc640 \uc5b4\ub5a4 \uc0dd\uba85\uc8fc\uae30\uae4c\uc9c0 \uace0\ub824\ub97c \ud558\uace0 \ub9cc\ub4e4\uc5b4\uc57c \uc6b0\ub9ac\uac00 \uc720\uc9c0\ubcf4\uc218\uac00 \uc88b\uc740 \ucf54\ub4dc\ub97c \uc9e4 \uc218 \uc788\ub294\uc9c0 \uc54c\uc544\ubcf4\uae30 \uc704\ud574 \uc791\uc131\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n## JavaScript \uc0dd\uba85\uc8fc\uae30\\r\\n\\r\\nJavaScript\uc5d0\uc11c \uc0ac\uc6a9\ub418\ub294 \ubcc0\uc218\ub098 \ud568\uc218 \ub4f1 \uc0dd\uc131\ub418\uace0 \uc18c\uba78\ub418\ub294 \uc77c\uc815\ud55c \uc0ac\uc774\ud074\uc774 \uc874\uc7ac\ud569\ub2c8\ub2e4. \uc774\ub807\uac8c JavaScript\uc5d0\uc11c \uc0dd\uc131 \ub41c **\ubcc0\uc218\ub098 \ud568\uc218\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \ubc94\uc704\ub97c \uc2a4\ucf54\ud504(Scope)** \ub77c\uace0 \uce6d\ud569\ub2c8\ub2e4.\\r\\n\\r\\n```javascript\\r\\nvar x = \\"global\\";\\r\\n\\r\\nfunction foo() {\\r\\n  var x = \\"function scope\\";\\r\\n  console.log(x);\\r\\n}\\r\\n\\r\\nfoo(); // ?\\r\\nconsole.log(x); // ?\\r\\n```\\r\\n\\r\\n\uc704\uc640 \uac19\uc740 \uc608\uc81c\ucc98\ub7fc `x`\uac00 2\ubc88 \uc120\uc5b8\ub418\ub294 \uacbd\uc6b0, JavaScript\uc5d0\uc11c\ub294 \uc5b4\ub5bb\uac8c \uad6c\ubd84\ud560 \uc218 \uc788\uc744\uae4c\uc694?\\r\\n\\r\\n\ubc14\ub85c **\uc2a4\ucf54\ud504(Scope)** \ub97c \ud1b5\ud574 `foo()` \ud568\uc218 \ub0b4\uc5d0 \uc120\uc5b8 \ub41c `x`\ub294 \ub0b4\ubd80\uc5d0\uc11c\ub9cc \ucc38\uc870\ud560 \uc218 \uc788\uc73c\uba70 \ucd5c\uc0c1\ub2e8 \uc120\uc5b8 \ub41c \ubcc0\uc218 `x`\uc758 \uacbd\uc6b0 \uc5b4\ub514\uc5d0\ub4e0 \ucc38\uc870\ud574\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \uc2a4\ucf54\ud504 \uaddc\uce59\uc744 \ud1b5\ud574\uc11c JavaScript \uc0dd\uba85 \uc8fc\uae30\ub97c \uad00\ub9ac\ud560 \uc218 \uc788\uace0 **\uc804\uc5ed \uc2a4\ucf54\ud504(Global Scope)** \uc640 **\uc9c0\uc5ed \uc2a4\ucf54\ud504(Local Scope)** \ub85c \ub098\ub20c \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### \uc804\uc5ed \uc2a4\ucf54\ud504(Global Scope)\uc640 \uc9c0\uc5ed \uc2a4\ucf54\ud504(Local Scope)\\r\\n\\r\\n\uc804\uc5ed\uc5d0 \ubcc0\uc218\ub97c \uc120\uc5b8\ud558\uba74 \uc774 \ubcc0\uc218\ub294 \uc5b4\ub514\uc11c\ub4e0\uc9c0 \ucc38\uc870\ud560 \uc218 \uc788\ub294 \uc804\uc5ed \uc2a4\ucf54\ud504\ub97c \uac16\ub294 \uc804\uc5ed \ubcc0\uc218\uac00 \ub429\ub2c8\ub2e4. `var` \ud0a4\uc6cc\ub4dc\ub85c \uc120\uc5b8\ud55c \uc804\uc5ed \ubcc0\uc218\ub294\xa0[\uc804\uc5ed \uac1d\uccb4(Global Object)](https://poiemaweb.com/js-built-in-object#21-%EC%A0%84%EC%97%AD-%EA%B0%9D%EC%B2%B4global-object)\xa0`window`\uc758 \ud504\ub85c\ud37c\ud2f0\uc785\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nvar global = \\"minsgy\\";\\r\\nfunction foo() {\\r\\n  var local = \\"msg\\";\\r\\n  console.log(global);\\r\\n  console.log(local);\\r\\n}\\r\\nfoo(); // minsgy msg\\r\\n\\r\\nconsole.log(global); // minsgy\\r\\nconsole.log(local); // Uncaught ReferenceError: local is not defined\\r\\n```\\r\\n\\r\\n\ubcc0\uc218 `global`\uc740 \ud568\uc218 \uc601\uc5ed \ubc16\uc5d0\uc11c \uc120\uc5b8\ub418\uc5b4 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc9c0\ub9cc \ubcc0\uc218 `local`\uc740 \uc2a4\ucf54\ud504\uac00 \uc885\ub8cc\ub418\uc5b4 \uc815\uc758\uac00 \uad6c\ud604\ub418\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \ub2e4\uc74c \uc608\uc2dc\ub97c \ud1b5\ud574 \ud568\uc218 \ub0b4\uc5d0 \uc874\uc7ac\ud558\ub294 \ub0b4\ubd80 \ud568\uc218\ub97c \uc0b4\ud3b4\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nvar x = \\"global\\";\\r\\nfunction foo() {\\r\\n  var x = \\"local\\";\\r\\n  console.log(x); // local\\r\\n  function bar() {\\r\\n    // \ub0b4\ubd80\ud568\uc218\\r\\n    console.log(x); // local\\r\\n  }\\r\\n  bar();\\r\\n}\\r\\n\\r\\nfoo();\\r\\nconsole.log(x); // global\\r\\n```\\r\\n\\r\\n\ub0b4\ubd80 \ud568\uc218\ub294 \uc790\uc2e0\uc744 \ud3ec\ud568\ud558\uace0 \uc788\ub294 \uc678\ubd80\ud568\uc218\uc758 \ubcc0\uc218\uc5d0 \uc811\uadfc\ud560 \uc218 \uc788\ub2e4\ub294 \ud2b9\uc9d5\uc774 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub294 \ub9e4\uc6b0 \uc720\uc6a9\ud558\uac8c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\xa0[\ud074\ub85c\uc800](https://developer.mozilla.org/ko/docs/Web/JavaScript/Closures)\uc5d0\uc11c\uc640 \uac19\uc774 \ub0b4\ubd80\ud568\uc218\uac00 \ub354 \uc624\ub798 \uc0dd\uc874\ud558\ub294 \uacbd\uc6b0 \ub2e4\ub978 \uc5b8\uc5b4\uc640\ub294 \ub2e4\ub974\uac8c \uc791\ub3d9\ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uacb0\uacfc\uc801\uc73c\ub85c \ud568\uc218 `bar`\uc5d0\uc11c \ucc38\uc870\ud558\ub294 `x`\ub294 \ud568\uc218 `foo`\uc5d0\uc11c \uc120\uc5b8 \ub41c \uc9c0\uc5ed\ubcc0\uc218\uc785\ub2c8\ub2e4. \uc774\ub294 \uc2a4\ucf54\ud504 \uccb4\uc778\uc5d0 \uc758\ud574 \ucc38\uc870 \uc21c\uc704\uac00 \uc804\uc5ed \ubcc0\uc218 `x`\uac00 \ub4a4\ub85c \ubc00\ub9b0 \uc0c1\ud0dc\uc774\uae30 \ub54c\ubb38\uc5d0 \uc7ac\ud560\ub2f9\ub418\uc5b4 `local` \uac12\uc774 \uc801\uc6a9\ub418\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uadf8\ub9ac\uace0 \uc120\uc5b8 \ub41c \uc704\uce58\uc5d0 \uc758\ud574 \ub530\ub77c \uc0dd\uba85 \uc8fc\uae30\uac00 \ub2ec\ub77c\uc9d1\ub2c8\ub2e4. **\uc774\ub97c \ub809\uc2dc\uceec \uc2a4\ucf54\ud504(Lexical Scope)** \ub77c\uace0 \ud569\ub2c8\ub2e4.\\r\\n\\r\\n### \ub809\uc2dc\uceec \uc2a4\ucf54\ud504(Lexical Scope)\\r\\n\\r\\n```js\\r\\nvar x = 1;\\r\\n\\r\\nfunction foo() {\\r\\n  var x = 10;\\r\\n  bar();\\r\\n}\\r\\n\\r\\nfunction bar() {\\r\\n  console.log(x);\\r\\n}\\r\\n\\r\\nfoo(); // 1\\r\\nbar(); // 1\\r\\n```\\r\\n\\r\\n\uc704 \ucf54\ub4dc\ub97c \ud1b5\ud574 \ub450 \uac00\uc9c0 \uacbd\uc6b0\ub97c \uc720\ucd94\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n> \ud568\uc218\ub97c \uc5b4\ub514\uc11c \ud638\ucd9c\ud558\uc600\ub294 \uc9c0? \ub3d9\uc801 \uc2a4\ucf54\ud504(Dynamic scope)\\r\\n> \ud568\uc218\ub97c \uc5b4\ub514\uc11c \uc120\uc5b8\ud558\uc600\ub294 \uc9c0? \ub809\uc2dc\uceec \uc2a4\ucf54\ud504(Lexical Scope)\\r\\n\\r\\n\uc5ec\uae30\uc11c JavaScript\ub294 \uae30\ubcf8\uc801\uc73c\ub85c \ub809\uc2dc\uceec \uc2a4\ucf54\ud504\ub97c \ub530\ub974\ubbc0\ub85c **\ud568\uc218\uac00 \uc120\uc5b8 \ub41c \uc2dc\uc810\uc5d0 \uc0c1\uc704 Scope\uac00 \uacb0\uc815**\ub418\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uacb0\uacfc\uc801\uc73c\ub85c \ud568\uc218\ub97c \uc5b4\ub514\uc5d0\uc11c \ud638\ucd9c\ud558\uc600\ub294\uc9c0 \uc2a4\ucf54\ud504 \uacb0\uc815\uc5d0\ub294 \uc544\ubb34\ub7f0 \uc758\ubbf8\ub3c4 \uc8fc\uc9c0 \uc54a\uc73c\uba70 \uc608\uc81c\uc640 \uac19\uc774 \ud568\uc218 `bar`\ub294 \uc804\uc5ed\uc5d0 \uc120\uc5b8\ub418\uc5b4\uc11c \uc804\uc5ed \ubcc0\uc218 \uac12 `x = 1`\uc744 2\ubc88 \ucd9c\ub825\ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uc704 \ud568\uc218\uc640 \uac19\uc774 \ud568\uc218\ub97c \ud638\ucd9c\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \ubc18\ud658\ud558\uba70 **\ud074\ub85c\uc838(Closure)**\ub97c \uc608\uc2dc\ub97c \ub4e4\uc5b4\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### \ud074\ub85c\uc838(Closure)\\r\\n\\r\\n```js\\r\\nfunction outerFunc() {\\r\\n  var x = 10;\\r\\n  var innerFunc = function () {\\r\\n    console.log(x);\\r\\n  };\\r\\n  return innerFunc;\\r\\n}\\r\\n/**\\r\\n * \ud568\uc218 outerFunc\ub97c \ud638\ucd9c\ud558\uba74 \ub0b4\ubd80 \ud568\uc218 innerFunc\uac00 \ubc18\ud658\ub41c\ub2e4.\\r\\n * \uadf8\ub9ac\uace0 \ud568\uc218 outerFunc\uc758 \uc2e4\ud589 \ucee8\ud14d\uc2a4\ud2b8\ub294 \uc18c\uba78\ud55c\ub2e4.\\r\\n * */\\r\\n\\r\\nvar inner = outerFunc();\\r\\ninner(); // 10\\r\\n```\\r\\n\\r\\n**\uc678\ubd80 \ud568\uc218 outerFunc**\ub294 **\ub0b4\ubd80 \ud568\uc218 innerFunc**\ub97c \ubc18\ud658\ud558\uace0 \uc0dd\uba85\uc8fc\uae30\ub97c \uc783\uac8c \ub429\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc outerFunc \uc9c0\uc5ed\ubcc0\uc218 `x`\ub97c \uc811\uadfc\ud560 \uc218 \uc788\ub294 \ubaa8\uc2b5\uc744 \ubcf4\uc785\ub2c8\ub2e4. \uc774\ub807\uac8c \ucc38\uc870\ub418\ub294 \uc678\ubd80\ud568\uc218(outerFunc) \ubcc0\uc218\ub97c **\uc790\uc720\ubcc0\uc218(Free variable)** \uc774\ub77c\uace0 \ubd80\ub985\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub807\uac8c \ucc38\uc870\uac00 \uac00\ub2a5\ud55c \uc774\uc720\ub294 \ub0b4\ubd80\ud568\uc218\uac00 \uc720\ud6a8\ud55c \uc0c1\ud0dc\uc5d0\uc11c \uc678\ubd80\ud568\uc218\uac00 \uc885\ub8cc\ud574 \ubc18\ud658\ub418\uc5b4\ub3c4 \uc678\ubd80 \ud568\uc218\ub0b4\uc758 \ud65c\uc131 \uac1d\uccb4(Activation object: \ubcc0\uc218, \ud568\uc218 \uc120\uc5b8 \uc815\ubcf4\ub97c \uac00\uc9c4)\ub294 **\ub0b4\ubd80 \ud568\uc218\uc5d0 \uc758\ud574 \ucc38\uc870\ub418\ub294 \ud55c \uc720\ud6a8\ud558\uba70 \uc2a4\ucf54\ud504 \uccb4\uc778\uc744 \ud1b5\ud574 \ucc38\uc870**\ud560 \uc218 \uc788\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/192228970-e36c0242-52d4-4cfe-93c3-f9c30698b421.png)\\r\\n\\r\\n\uc774\uc640 \uac19\uc774 **\ubc18\ud658 \ub41c \ub0b4\ubd80 \ud568\uc218\uac00 \uc790\uc2e0\uc774 \uc120\uc5b8\ub410\uc744 \ub54c\uc758 \ud658\uacbd\uc778 \uc2a4\ucf54\ud504\ub97c \uae30\uc5b5\ud558\uc5ec, \uc774\uc804 \ud658\uacbd \ubc16\uc5d0\uc11c \ud638\ucd9c\ub418\uc5b4\ub3c4 \uadf8 \ud658\uacbd\uc5d0 \uc811\uadfc\ud560 \uc218 \uc788\ub294 \ud568\uc218**\ub97c **\ud074\ub85c\uc838(Closure)** \ub77c\uace0 \ud569\ub2c8\ub2e4.\\r\\n\\r\\n## \ud074\ub85c\uc838\ub294 \uc5b4\ub514\uc5d0 \uc0ac\uc6a9\ud558\ub098\uc694?\\r\\n\\r\\n\ucf54\ub4dc\uc758 \ubcf5\uc7a1\uc131\uc744 \uc904\uc77c \uc218\ub3c4 \uc788\uc9c0\ub9cc \uc790\uce6b\ud558\uba74 \ucc38\uc870\ub418\ub294 \uc0c1\ud669\uc774 \ubc1c\uc0dd\ud574 **GC(Garbage Collector)** \uac00 \ubc1c\uc0dd\ud558\uc9c0 \uc54a\ub294 \ubb38\uc81c\uac00 \ub098\ud0c0\ub0a0 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \uc810\ub4e4\uc744 \uace0\ub824\ud558\uba74\uc11c \ud074\ub85c\uc800\ub97c \ud65c\uc6a9\ud55c \uc0ac\ub840\ub97c \ud655\uc778\ud574\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### 1. \uc804\uc5ed \ubcc0\uc218 \uc904\uc774\uae30\\r\\n\\r\\n```js\\r\\n// \uae30\uc874\\r\\nconst likeButton = document.querySelector(\\"button\\");\\r\\nlikeButton.addEventListener(\\"click\\", handleClick);\\r\\n\\r\\nlet count = 0; // \uc804\uc5ed \ubcc0\uc218 \uc120\uc5b8\uc73c\ub85c \uc778\ud55c \ubb38\uc81c\ub4e4\uc774 \ub9ce\uc74c\\r\\nfunction handleClick() {\\r\\n  count++;\\r\\n  return count;\\r\\n}\\r\\n\\r\\n// \ud074\ub85c\uc838 \uc0ac\uc6a9\\r\\nconst likeButton = document.querySelector(\\"button\\");\\r\\nlikeButton.addEventListener(\\"click\\", handleClick());\\r\\n\\r\\nfunction handleClick() {\\r\\n  let count = 0;\\r\\n  // \ub809\uc2dc\uceec \ud658\uacbd\uc744 \ucc38\uc870\ud558\ub294 \ud568\uc218 likeButton\uc758 callback \ud568\uc218\ub97c \ud65c\uc6a9\ud558\uc5ec \uc804\uc5ed \ubcc0\uc218 \uc5c6\uc774 \uad6c\ud604\\r\\n  return function () {\\r\\n    count++;\\r\\n    return count;\\r\\n  };\\r\\n}\\r\\n```\\r\\n\\r\\n\ud074\ub85c\uc838\ub97c \uc0ac\uc6a9\ud558\uc5ec \uc544\ub798 \ub85c\uc9c1\uacfc \uac19\uc774 \ubaa8\ub4c8\ud654\ud55c \ud568\uc218\uc5d0\uc11c\ub9cc \uc0ac\uc6a9\ud558\ub294 \uc885\uc18d\uc801\uc778 \ubcc0\uc218\ub97c \uc120\uc5b8\ud569\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 **\uba54\uc18c\ub4dc(Method) \uc5ed\ud560\uc774 \uad6c\ubd84**\ub418\uc5b4 \uc0ac\uc6a9\ud560 \uc218\ub3c4 \uc788\uc744 \ubfd0\ub9cc \uc544\ub2c8\ub77c \uae30\ubcf8\uc801\uc778 \uc804\uc5ed \uc2a4\ucf54\ud504\uc5d0 \ub300\ud55c \ubb38\uc81c\uac00 \ud574\uacb0\ub418\uc5b4 **\uc778\ud130\ud398\uc774\uc2a4(Interface) \ubcf5\uc7a1\uc131\uc774 \ub0ae\uc544\uc9c0\uac8c \ub429\ub2c8\ub2e4.**\\r\\n\\r\\n### 2. \ud568\uc218\ud615 \ubc29\uc2dd \ubaa8\ub4c8\ud654\\r\\n\\r\\n\ud074\ub85c\uc800\ub294 **\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d\uc758 \uc77c\uae09 \uac1d\uccb4(first-class) \uac1c\ub150\uc744 \uc778\uc6a9\ud558\uc5ec \uc2a4\ucf54\ud504(Scope)\uc5d0 \ubb36\uc778 \ubcc0\uc218\ub97c \ubc14\uc778\ub529\ud558\uae30 \uc704\ud55c \uc77c\uc885\uc758 \uae30\uc220**\uc785\ub2c8\ub2e4. \uc774\ub7ec\ud55c \uc77c\uae09 \uac1d\uccb4 \uac1c\ub150\uc744 \ud65c\uc6a9\ud558\uc5ec \ud568\uc218\ub97c \uc800\uc7a5\ud55c \ub808\ucf54\ub4dc(Record) \uc5ed\ud560\ub85c \ud65c\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nfunction makeSizer(size) {\\r\\n  return function () {\\r\\n    document.body.style.fontSize = size + \\"px\\";\\r\\n  };\\r\\n}\\r\\n\\r\\nvar size12 = makeSizer(12);\\r\\nvar size14 = makeSizer(14);\\r\\nvar size16 = makeSizer(16);\\r\\n```\\r\\n\\r\\n\uc704\uc640 \uac19\uc740 \ud568\uc218\ud615 \uac1c\ub150\uc744 \ud1b5\ud574 \uc678\ubd80 \ud568\uc218\uc758 \ud30c\ub77c\ubbf8\ud130\ub97c \uac16\uace0 \ub2e4\ub978 \uc694\uc18c\uc5d0 \uc758\ud574 \ub113\uc740 \ud655\uc7a5\uc131\uc744 \uac00\uc9c8 \ubfd0\ub9cc \uc544\ub2c8\ub77c \ub0b4\ubd80\uc5d0 \ub4e4\uc5b4\uac00\ub294 \ub85c\uc9c1\uc744 \uac10\ucd9c \uc218 \uc788\ub2e4\ub294 Private\ud55c \ud2b9\uc9d5\uc744 \uac00\uc9c0\uac8c \ub429\ub2c8\ub2e4.\\r\\nJavaScript \ud0dc\uc0dd\uc5d0 \uc874\uc7ac\ud558\uc9c0 \uc54a\ub294 \uba54\uc18c\ub4dc\ub97c \uad6c\ud604\ud558\uc5ec \uc81c\ud55c\uc801\uc778 \uc811\uadfc\ub9cc \ud5c8\uc6a9\\\\*\\\\*\ud560 \uc218 \uc788\uac8c \ub9cc\ub4ed\ub2c8\ub2e4.\\r\\n\\r\\n### 3. React Hook\\r\\n\\r\\n```jsx\\r\\nimport React, { useEffect, useState, useCallback } from \\"react\\";\\r\\n\\r\\n// Timer\ub97c \ub098\ud0c0\ub0b4\ub294 \ucef4\ud3ec\ub10c\ud2b8\\r\\nconst TimerComponent = () => {\\r\\n  const [count, setCount] = useState(1);\\r\\n\\r\\n  const incrementCount = useCallback(() => {\\r\\n    setCount(count + 1);\\r\\n  }, []); // empty dependancy\\r\\n\\r\\n  useEffect(() => {\\r\\n    const timer = setTimeout(() => {\\r\\n      incrementCount();\\r\\n    }, 1000);\\r\\n\\r\\n    return () => {\\r\\n      clearTimeout(timer);\\r\\n    };\\r\\n  }, []); // empty dependancy\\r\\n\\r\\n  // \uacb0\uacfc\ub294 2\uc5d0\uc11c \uba48\ucd98\ub2e4.\\r\\n  return <div>{`Timer started: ${count}`}</div>;\\r\\n};\\r\\n```\\r\\n\\r\\n\ud574\ub2f9 \ub85c\uc9c1\uc740 \ub9e4 \ucd08\ub9c8\ub2e4 \uac12\uc774 1\uc529 \uc99d\uac00\ud558\ub294 \ud0c0\uc774\uba38 \ucef4\ud3ec\ub10c\ud2b8\uc785\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc758\ub3c4\ud55c\ub300\ub85c \uc791\ub3d9\ud558\uc9c0 \uc54a\uace0 2\uc5d0\uc11c \ud0c0\uc774\uba38\uac00 \uba48\ucd94\uac8c \ub429\ub2c8\ub2e4. \uc758\uc874\uc131 \ubc30\uc5f4\uc5d0 \uc758\ud574, \ud55c\ubc88\uc758 \ub80c\ub354\ub9c1\ub9cc \uc77c\uc5b4\ub098\uac8c \ub418\uc5b4 \ud568\uc218\uac00 \uae30\uc5b5\ud558\ub294 \uac12\uc740 \uc5ec\uc804\ud788 1\uc774\uae30 \ub54c\ubb38\uc5d0 \ud0c0\uc774\uba38\uac00 \ubcc0\ud558\uc9c0 \uc54a\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uc989, useEffect hook \uc2e4\ud589 \uc2dc\uc810\uc5d0 incrementCount \ud568\uc218\ub294 count\uac00 1\uc778 \ud658\uacbd\uc744 \uae30\uc5b5\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ud6c4 count\uac00 \uc5c5\ub370\uc774\ud2b8 \ub418\uc5b4 \ud568\uc218\uac00 \uc0c8\ub85c \ubcc0\uacbd\ub418\uc5b4\ub3c4 useEffect\uc5d0\uc11c \ucc98\uc74c 1\uc744 \uae30\uc5b5\ud558\ub294 \ud568\uc218\ub97c \uc2e4\ud589\ud558\uac8c \ub429\ub2c8\ub2e4. \uc774\ub85c \uc778\ud574 count \uac12\uc774 \uc99d\uac00\ud558\uc9c0 \uc54a\uac8c \ub418\uac70\ub098 **useEffect\uac00 \uc2e4\ud589\ub418\uc9c0 \uc54a\uc544 Timer \uac12\uc774 \ubcc0\ud654\uac00 \uc5c6\uac8c \ub418\ub294 \uacb0\uacfc**\ub97c \ubcf4\uc785\ub2c8\ub2e4.\\r\\n\\r\\nuseEffect \ubfd0\ub9cc \uc544\ub2c8\ub77c useCallback\uc5d0\uc11c\ub3c4 \ud074\ub85c\uc800 \uac1c\ub150\uc744 \ud65c\uc6a9\ud558\uc5ec \ub809\uc2dc\uceec \ud658\uacbd\uc744 \uad00\ub9ac\ud558\uac8c \ub429\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 Hook\uc744 \uc0ac\uc6a9 \ud560 \uc2dc \uc758\uc874\uc131 \ubc30\uc5f4 \uad00\ub9ac\ub97c \ud1b5\ud574 \ub809\uc2dc\uceec \ud658\uacbd\uc744 \ubcc0\ud654\uc2dc\ud0ac \uc218 \uc788\ub2e4\ub294 \uc810\uc5d0 \uc788\uc5b4\uc11c \uc758\ub3c4\ud55c \uacb0\uacfc\ub97c \ub0b4\ub3c4\ub85d \uc8fc\uc758\ud574\uc57c \ud569\ub2c8\ub2e4. useState\uc758 \uc5c5\ub370\uc774\ud2b8 \ubc29\uc2dd\ub3c4 \ud074\ub85c\uc800\ub97c \ud65c\uc6a9\ud569\ub2c8\ub2e4.\\r\\n\\r\\n### bonus. useState\uc758 \uc5c5\ub370\uc774\ud2b8 \uad6c\ud604 \ubc29\uc2dd\\r\\n\\r\\n```jsx\\r\\nlet _value;\\r\\n\\r\\nexport useState(initialValue){\\r\\n  if (_value === \'undefined\') {\\r\\n    _value = initialValue;\\r\\n  }\\r\\n  const setValue = newValue => {\\r\\n    _value = newValue;\\r\\n  }\\r\\n\\r\\n  return [_value, setValue];\\r\\n}\\r\\n```\\r\\n\\r\\nuseState \ubc16\uc5d0 \uc120\uc5b8\ub41c \ubcc0\uc218\xa0`_value`\uac00 \uc788\uc2b5\ub2c8\ub2e4. useState\uc5d0\uc11c\ub294 \ucd08\uae30\uac12(initialValue)\ub97c \ubc1b\uc544 \ub9cc\uc57d \uae30\uc874\xa0`_value`\xa0\uac12\uc774 \uc5c6\uc73c\uba74 \ucd08\uae30\uac12\uc73c\ub85c \uc138\ud305\ud558\uac8c \ub429\ub2c8\ub2e4. setValue \ud568\uc218\ub294 \ubc1b\uc544\uc624\ub294 \uac12\uc73c\ub85c \uc804\uc5ed\xa0`_value`\ub97c \uc5c5\ub370\uc774\ud2b8\ud558\uac8c \ub418\uba74\uc11c \xa0`_value`\uc640\xa0`setValue`\xa0\ud568\uc218\ub97c \ubc30\uc5f4 \ud615\ud0dc\ub85c \ubc18\ud658\ud55c\ub2e4. useState\uac00 \uc5b4\ub514\uc5d0\uc11c \uc2e4\ud589\ub418\uc5c8\uac74, \ud074\ub85c\uc800\ub97c \ud1b5\ud574\xa0`_value`\xa0\uac12\uc5d0 \uc811\uadfc\ud560 \uc218 \uc788\ub294 \uad6c\uc870\ub97c \uac00\uc9c0\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uacb0\uacfc\uc801\uc73c\ub85c useState\ub97c \ud1b5\ud574 \uc0dd\uc131\ud55c \uc0c1\ud0dc\ub97c \uc811\uadfc\ud558\uace0 \uc720\uc9c0\ud558\uae30 \uc704\ud574\uc11c useState \ubc14\uae65\ucabd\uc5d0 state\ub97c \uc800\uc7a5\ud558\uc5ec \uc120\uc5b8 \ub41c \ucef4\ud3ec\ub10c\ud2b8\ub97c \uad6c\ubcc4\ud560 \uc218 \uc788\ub294 key\ub85c \uc811\uadfc\ud558\uac8c \ub418\uace0 \ubc30\uc5f4 \ud615\uc2dd\uc73c\ub85c \uc800\uc7a5\ub418\uac8c \ub429\ub2c8\ub2e4. useState \uc548\uc5d0\uc11c \uc120\uc5b8\ub418\ub294 \uc0c1\ud0dc\ub4e4\uc740 `_value` \ubc30\uc5f4\uc5d0 \uc21c\uc11c\ub300\ub85c \uc800\uc7a5\ub418\ub294 \uc6d0\ub9ac\uc785\ub2c8\ub2e4. (\uc608\uc2dc\ub294 \uc784\uc758 \uac12\uc774\uae34 \ud558\ub2e4..)\\r\\n\\r\\n## Reference\\r\\n\\r\\n[useState\uc640 \ud074\ub85c\uc800](https://yeoulcoding.tistory.com/149#recentEntries)  \\r\\n[\ud074\ub85c\uc800 MDN](https://developer.mozilla.org/ko/docs/Web/JavaScript/Closures)  \\r\\n[\ud074\ub85c\uc800\uc5d0 \ub300\ud558\uc5ec](https://poiemaweb.com/js-closure)"},{"id":"JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30","metadata":{"permalink":"/blog/JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30","editUrl":"https://github.com/minsgy/minsgy.github.io/edit/main/blog/javascript-memory.md","source":"@site/blog/javascript-memory.md","title":"JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30","description":"\uc11c\ub860","date":"2022-09-18T10:51:29.000Z","formattedDate":"2022\ub144 9\uc6d4 18\uc77c","tags":[{"label":"JavaScript","permalink":"/blog/tags/java-script"},{"label":"DeepDive","permalink":"/blog/tags/deep-dive"},{"label":"Study","permalink":"/blog/tags/study"}],"readingTime":13.115,"hasTruncateMarker":false,"authors":[{"name":"Choi Minseok","title":"\uc3d8\uce74(SOCAR)\uc5d0\uc11c \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc744 \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.","url":"https://github.com/minsgy","imageURL":"https://github.com/minsgy.png","key":"minsgy"}],"frontMatter":{"slug":"JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30","title":"JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30","authors":["minsgy"],"tags":["JavaScript","DeepDive","Study"]},"prevItem":{"title":"JavaScript Scope\uc640 Closure","permalink":"/blog/JavaScript Scope\uc640 Closure"},"nextItem":{"title":"JavaScript Runtime\uc5d0 \ub300\ud558\uc5ec","permalink":"/blog/JavaScript Runtime\uc5d0 \ub300\ud558\uc5ec"}},"content":"## \uc11c\ub860\\r\\n\\r\\nJavaScript\uc758 \uba54\ubaa8\ub9ac \uad00\ub9ac\ub294 \uc5b4\ub824\uc6b4 \uc8fc\uc81c\uc785\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4 JavaScript\ub294 GC(Garbage Collector)\ub97c \uc0ac\uc6a9\ud558\uace0 \uc788\uc9c0\ub9cc, GC\uac00 \uc5b8\uc81c \uc5b4\ub5a4 \uba54\ubaa8\ub9ac\ub97c \ud574\uc81c\ud560\uc9c0\ub294 \uc54c \uc218 \uc5c6\uc2b5\ub2c8\ub2e4. JavaScript\uc5d0\uc11c \uba54\ubaa8\ub9ac \uad00\ub9ac\ub97c \uc798 \ud558\ub824\uba74 \uc5b4\ub5a4 \uac83\ub4e4\uc744 \uc54c\uc544\uc57c \ud560 \uc9c0 \uc54c\uc544\ubcf4\uae30 \uc704\ud574 \uc791\uc131\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n## JavaScript \ub370\uc774\ud130\uc640 \uba54\ubaa8\ub9ac\\r\\n\\r\\nJavaScript\uc5d0\uc11c \ub370\uc774\ud130\ub294 \ud06c\uac8c 2\uac00\uc9c0\ub85c \ub098\ub20c \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n**\ubcc0\uacbd \ubd88\uac00\ub2a5\ud55c \uac12(immutable value)\uc778 \uc6d0\uc2dc \ud0c0\uc785**\uacfc **\ubcc0\uacbd \uac00\ub2a5\ud55c(mutable value) \uac1d\uccb4 \ud0c0\uc785**\uc73c\ub85c \uad6c\ubd84\ud560 \uc218 \uc788\uc73c\uba70 \uc815\uc801 \ub370\uc774\ud130(Static Data)\ub97c \uc800\uc7a5\ud558\ub294 \ub370\uc774\ud130 \uad6c\uc870\uc778 \uc2a4\ud0dd(Stack)\uacfc \ub3d9\uc801 \ub370\uc774\ud130 \ud560\ub2f9\ud558\ub294 \ud799(Heap)\uc774 \uc874\uc7ac\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub97c \uc880 \ub354 \uc790\uc138\ud558\uac8c \uc54c\uc544\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### \uba54\ubaa8\ub9ac \uc0dd\uba85\uc8fc\uae30 \uacfc\uc815\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/190898884-3f7d3777-8e62-4429-baf2-371f0ae23d66.png)\\r\\n\uac04\ub2e8\ud558\uac8c \ud45c\ud604\ud558\uc5ec **JavaScript**\uc5d0\uc11c \ubcc0\uc218\ub098 \ud568\uc218\ub97c \uc0ac\uc6a9\ud558\uace0 \ud544\uc694\uac00 \uc5c6\uc5b4\uc9c0\uba74 \uba54\ubaa8\ub9ac\uc5d0\uc11c \ud574\uc81c\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\uc0dd\uc131\ud55c \uac1d\uccb4(Object)\uc5d0 \ud544\uc694\ud55c **\uba54\ubaa8\ub9ac\ub97c \ud560\ub2f9**\ud558\uace0, \uc791\uc131\ud55c \ucf54\ub4dc\ub97c \ud1b5\ud574 \ubcc0\uc218\ub97c \uc77d\uac70\ub098 \uc0ac\uc6a9\ud558\ub294 \uacfc\uc815\uc73c\ub85c **\uba54\ubaa8\ub9ac\ub97c \uc0ac\uc6a9**\ud569\ub2c8\ub2e4. \uc774\ud6c4 JS \uc5d4\uc9c4\uc744 \ud1b5\ud574 **\ud560\ub2f9 \ub41c \uba54\ubaa8\ub9ac\uac00 \ud574\uc81c**\ub418\uc5b4 \uba54\ubaa8\ub9ac\ub97c \uc0ac\uc6a9\uac00\ub2a5\ud55c \uc0c1\ud0dc\ub85c \ub9cc\ub4e4\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n### \uc6d0\uc2dc \ud0c0\uc785 (Primitive Type)\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/190897442-875ae942-8993-41f0-b4f5-c73a3b2e16a3.png)\\r\\n\\r\\n\uc6d0\uc2dc \ud0c0\uc785\uc758 \uc885\ub958\ub85c `string`, `number`, `boolean`, `undefined`, `null`, `symbol`, \ucc38\uc870\\r\\n\\r\\n\uc5ec\uae30\uc11c \ub9d0\ud558\ub294 \uc6d0\uc2dc \uac12\uc758 \ubcc0\uacbd\uc774 \ubd88\uac00\ub2a5\ud558\ub2e4\ub294 \uac74, **\\"\uba54\ubaa8\ub9ac \uc0c1 \ud560\ub2f9 \ub41c \ubcc0\uc218 \uac12\\"** \uc744 \ubcc0\uacbd\ud560 \uc218 \uc5c6\ub2e4\ub294 \uc758\ubbf8\uc785\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub7ec\ud55c \ud2b9\uc9d5\uc73c\ub85c \uc6d0\uc2dc \ud0c0\uc785\uc740 `\ubd88\ubcc0\uc131` \ud2b9\uc9d5\uc744 \uac16\uac8c \ub418\uace0, \uac12\uc744 \ubcf5\uc0ac\ud55c\ub2e4\uba74 \uc0c8\ub85c\uc6b4 \uba54\ubaa8\ub9ac \uc8fc\uc18c\ub97c \ud560\ub2f9\ud558\uc5ec **call by value** \ubc29\uc2dd\uc73c\ub85c \ubcf5\uc0ac\ub418\uc5b4 \uc0ac\uc6a9\ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uc704\uc640 \uac19\uc740 \uc758\ubbf8\ub85c \uc544\ub798\uc640 \uac19\uc774 \ucf54\ub4dc\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// \uae30\uc874 \uc800\uc7a5 \ub41c \uba54\ubaa8\ub9ac\uc5d0 \uac12\uc774 \uc800\uc7a5\ub418\ub294 \uac83\uc774 \uc544\ub2cc \uc0c8\ub85c\uc6b4 \uba54\ubaa8\ub9ac \uc8fc\uc18c\ub97c \ud560\ub2f9\ud569\ub2c8\ub2e4.\\r\\nlet example = 3;\\r\\nexample = 5;\\r\\nconsole.log(example); // 5\\r\\n\\r\\n// \uac19\uc740 \uac12\uc744 \uac00\uc9c0\uace0 \uc788\uc9c0\ub9cc, \uc11c\ub85c \ub2e4\ub978 \uba54\ubaa8\ub9ac \uc8fc\uc18c\ub97c \uac16\uace0 \uc788\ub294 call by value\\r\\nlet a = 1;\\r\\nlet b = a;\\r\\n\\r\\nconsole.log(a === b); // true\\r\\n```\\r\\n\\r\\n\uc774\uc5b4\uc11c \uac1d\uccb4(object)\uc640 \ud568\uc218(function)**\ub294 \ud799(heap)\uc5d0 \uc800\uc7a5\ub418\uc9c0\ub9cc, \ucc38\uc870(Reference)\ub294 \uc2a4\ud0dd(Stack)\uc5d0 \uc800\uc7a5\ud558\uc5ec \ud65c\uc6a9**\ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n### \uac1d\uccb4(\ucc38\uc870) \ud0c0\uc785 (Object Type)\\r\\n\\r\\n\uac1d\uccb4\ub294 \ubcc0\uacbd \uac00\ub2a5\ud55c \uac12(mutable value)\uc785\ub2c8\ub2e4.\\r\\n\\r\\n\uc55e\uc11c \uc0ac\uc6a9\ud558\ub294 `const` \ud0a4\uc6cc\ub4dc\ub294 \uc0c1\uc218\ub97c \ub9cc\ub4dc\ub294 \uac78 \ubaa9\uc801\uc73c\ub85c \ud569\ub2c8\ub2e4.\\r\\n\uc120\uc5b8\ud55c \ubcc0\uc218 \uc7ac\ud560\ub2f9\uc774 \ubd88\uac00\ub2a5\ud558\uc9c0\ub9cc **\uac1d\uccb4\uc5d0 \ud560\ub2f9\ud55c \ubcc0\uc218(property)** \ub294 \ubcc0\uacbd \uac00\ub2a5\ud569\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nconst a = {};\\r\\na = 3; // @ERROR \uc0c1\uc218\ub85c \uc120\uc5b8\ub418\uc5b4 \uc7ac\ud560\ub2f9\uc774 \ubd88\uac00\ub2a5\ud569\ub2c8\ub2e4.\\r\\na.name = \\"\ucd5c\ubbfc\uc11d\\"; // \uac1d\uccb4\ub97c \ud560\ub2f9\ud55c \ubcc0\uc218\uc5d0 \uc7ac\ud560\ub2f9\ud558\ub294 \uac83\uc774 \uc544\ub2c8\uae30\ub54c\ubb38\uc5d0 \uac00\ub2a5\ud569\ub2c8\ub2e4.\\r\\nconsole.log(a); // { name: \\"\ucd5c\ubbfc\uc11d\\" }\\r\\n```\\r\\n\\r\\n\uac1d\uccb4\ub97c \uc0dd\uc131\ud558\uace0 \uad00\ub9ac\ud558\ub294 \ubc29\ubc95\uc740 \ube44\uc6a9\uc774 \ub9ce\uc774 \ub4e4\uac8c \ub429\ub2c8\ub2e4. \uadf8\ub798\uc11c **\uac1d\uccb4\ub97c \ubcc0\uacbd \uac00\ub2a5\ud55c \uac12\uc73c\ub85c \uc124\uacc4**\ud588\uc2b5\ub2c8\ub2e4.\\r\\nJavaScript\uc5d0\uc11c\ub294 \uc774\ub97c \ucd5c\uc18c\ud654\ud558\uc5ec **\uba54\ubaa8\ub9ac \ud6a8\uc728\uc801 \uc18c\ube44\ub97c \ub192\uc774\uace0 \uc131\ub2a5\uc744 \uac1c\uc120**\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\uadf8\ub7ec\ub098 **\uc5ec\ub7ec \uac1c\uc758 \uc2dd\ubcc4\uc790\uac00 \ud558\ub098\uc758 \uac1d\uccb4\ub97c \uacf5\uc720**\ud558\uc5ec \uc601\ud5a5\uc744 \ub07c\uce58\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4.\\r\\n\\r\\n### \ucc38\uc870\uac12\uc73c\ub85c \uc778\ud55c \ubb38\uc81c\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/190900382-bfc3ab00-be1f-4195-80e8-048a374833ac.png)\\r\\n\\r\\n\uadf8\ub9bc\uacfc \uac19\uc774 `getDeveloper()` \ud568\uc218\ub294 `developer` \uac1d\uccb4\uc758 `name` property\ub97c \uac00\ub9ac\ud0a4\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\uc774\ub97c \ud1b5\ud574 \ubc1c\uc0dd\ud558\ub294 \ubb38\uc81c\uc810\uc744 \ud655\uc778\ud574\ubd05\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// \uc11c\ub85c \ub2e4\ub978 \uac12\uc774 \uc601\ud5a5\uc744 \ubc1b\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4.\\r\\n// \uc774\ub97c \ud1b5\ud574 \ucf54\ub4dc \ubcf5\uc7a1\uc131\uc774 \ub298\uc5b4\ub098\uac8c \ub429\ub2c8\ub2e4.\\r\\nconst developer = {\\r\\n  name: \\"minsgy\\",\\r\\n  age: \\"26\\",\\r\\n};\\r\\n\\r\\nfunction getDeveloperName() {\\r\\n  return developer.name;\\r\\n}\\r\\nconsole.log(getDeveloperName()); // \\"minsgy\\"\\r\\ndeveloper.name = \\"umin\\";\\r\\nconsole.log(getDeveloperName()); // \\"umin\\"\\r\\n```\\r\\n\\r\\n`call by reference`\ub97c \uae30\ubc18\ud558\uc5ec \ub2e4\uc911 \uc2dd\ubcc4\uc790\uc5d0 \ub300\ud55c \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\ud558\ub098\uc758 \ub85c\uc9c1\uc5d0 **\uc758\uc874\uc131(dependency)** \uc744 \uac00\uc9c0\uace0 \ucf54\ub4dc \ubcf5\uc7a1\uc131\uc774 \ub192\uc544\uc9c0\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud558\uace0 \uc758\ub3c4\ud558\uc9c0 \uc54a\uc740 \uac12 \ubcc0\uacbd\uc774 \uc77c\uc5b4\ub09c\ub2e4\uba74 \ub514\ubc84\uae45\uc5d0\ub3c4 \uc5b4\ub824\uc6c0\uc744 \uac00\uc838 **\uc720\uc9c0\ubcf4\uc218\uc5d0 \uc788\uc5b4\uc11c\ub3c4 \uc88b\uc9c0 \uc54a\uc740 \uacb0\uacfc**\ub97c \ubcf4\uc5ec\uc90d\ub2c8\ub2e4.\\r\\n\\r\\n### \uba54\ubaa8\ub9ac \ub204\uc218\uac00 \ubc1c\uc0dd\ud558\ub294 \uacbd\uc6b0\\r\\n\\r\\nJavaScript\ub294 \ud560\ub2f9\ub41c \uba54\ubaa8\ub9ac\ub97c \uc0ac\uc6a9\ud558\uc9c0 \uc54a\ub294 \uacbd\uc6b0 [GC(Garbage collector)](https://developer.mozilla.org/ko/docs/Web/JavaScript/Memory_Management)\uc5d0 \uc758\ud574 \uba54\ubaa8\ub9ac \ud560\ub2f9\uc744 \ucd94\uc801\ud558\uace0 \uc790\ub3d9\uc73c\ub85c \uba54\ubaa8\ub9ac\ub97c \ubc18\ud658\uc2dc\ud0a4\ub294 \uc5ed\ud560\uc744 \ud558\uac8c \ub429\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc774\ub7ec\ud55c \uacfc\uc815\uc5d0 \uc788\uc5b4\uc11c \uba54\ubaa8\ub9ac\uac00 \uc0ac\uc6a9\ub418\ub294 \uc9c0 \ucd94\uc815\ud558\uae30 \ub54c\ubb38\uc5d0 \ub54c\ub54c\ub85c **\uacb0\uc815\ubd88\uac00\ub2a5(undecidable) \uc0c1\ud0dc**\uac00 \ubc1c\uc0dd\ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\ub300\ubd80\ubd84\uc758 GC\ub294 \ubaa8\ub4e0 \ubcc0\uc218\uac00 \uc2a4\ucf54\ud504(Scope)\ub97c \ubc97\uc5b4\ub0ac\uc744 \ub54c \ub354 \uc774\uc0c1 \uc811\uadfc \ubd88\uac00\ub2a5\ud55c \uba54\ubaa8\ub9ac\ub97c \uc218\uc9d1\ud558\uc9c0\ub9cc \uc2a4\ucf54\ud504\uac00 \uc720\uc9c0\ub418\ub294 \uacbd\uc6b0\uac00 \uc0dd\uae34\ub2e4\uba74 \uba54\ubaa8\ub9ac\ub97c \ubc18\ud658\ud558\uc9c0 \uc54a\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uacb0\uad6d GC\uac00 \uc758\uc874\ud558\ub294 \uc54c\uace0\ub9ac\uc998\uc740 **\uc5ec\ub7ec \uac1d\uccb4\uc640\uc758 \ucc38\uc870(Reference)** \ub97c \ud1b5\ud55c \uac1c\ub150\uc785\ub2c8\ub2e4.\\r\\n\uc774\ub85c \uc778\ud574 \ubc1c\uc0dd\ud560 \uc218 \uc788\ub294 \ubb38\uc81c\ub294 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n#### \uc21c\ud658\ucc38\uc870 (\ud574\uacb0)\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/190912024-41c28c81-2deb-46e1-9596-49e1d1eea2e8.png)\\r\\n\\r\\n\uc544\ub798 \uc608\uc81c\uc5d0\uc11c \ub450 \uac1d\uccb4\uac00 \uc0dd\uc131\ub418\uac8c \ub418\uba74\uc11c \uc11c\ub85c\ub97c \ucc38\uc870\ud558\uace0 **\uc21c\ud658\ucc38\uc870\uac00 \uc0dd\uc131**\ub418\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uc0ac\uc2e4\uc0c1 \ud568\uc218 \ud638\ucd9c \uc774\ud6c4, \uc2a4\ucf54\ud504(Scope)\ub97c \ubc97\uc5b4\ub098\uac8c \ub418\uba74\uc11c \uc0ac\uc6a9\ud558\uc9c0 \uc54a\uac8c \ub418\uc9c0\ub9cc \ub450 \uac1d\uccb4 \ub2e4 \ud55c \ubc88\uc740 \ucc38\uc870\ud55c \uac78\ub85c \uac04\uc8fc\ub418\uc5b4 GC(Gabage collector)\uac00 \uc801\uc6a9\ub418\uc9c0 \uc54a\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nfunction f() {\\r\\n  var o1 = {};\\r\\n  var o2 = {};\\r\\n  o1.p = o2; // o1\uc740 o2\ub97c \ucc38\uc870\ud568\\r\\n  o2.p = o1; // o2\ub294 o1\uc744 \ucc38\uc870\ud568. \uc774\ub97c \ud1b5\ud574 \uc21c\ud658 \ucc38\uc870\uac00 \ub9cc\ub4e4\uc5b4\uc9d0.\\r\\n}\\r\\n\\r\\nf();\\r\\n```\\r\\n\\r\\n2012\ub144 \uae30\uc900\uc73c\ub85c \ud604\ub300 \ube0c\ub77c\uc6b0\uc800\ub294 \ud574\ub2f9 \uc21c\ud658\ucc38\uc870\ub97c \ud574\uacb0\ud560 \uc218 \uc788\ub294 [Mark-Sweep \uc54c\uace0\ub9ac\uc998](https://www.geeksforgeeks.org/mark-and-sweep-garbage-collection-algorithm/)\uc774 \uc801\uc6a9\ub418\uba74\uc11c \uc21c\ud658\ucc38\uc870 \ubb38\uc81c\uac00 \ud574\uacb0\ub418\uc5c8\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc React\uc640 \uac19\uc774 \ucef4\ud3ec\ub10c\ud2b8 \uac04 \uc21c\ud658 \ucc38\uc870\uac00 \uc77c\uc5b4\ub0a0 \uacbd\uc6b0 \uc774\uc288\uac00 \ubc1c\uc0dd\ud560 \uc218 \uc788\uae30 \ub54c\ubb38\uc5d0 **\uc5ec\uc804\ud788 \uc9c0\uc591\ud574\uc57c \ud558\ub294 \ubd80\ubd84**\uc785\ub2c8\ub2e4.\\r\\n\\r\\n#### \uc804\uc5ed \ubcc0\uc218\\r\\n\\r\\n\uc120\uc5b8\ub418\uc9c0 \uc54a\ub294 \ubcc0\uc218\ub97c \ucc38\uc870\ud558\uac8c \ub41c\ub2e4\uba74 \uc804\uc5ed \uac1d\uccb4\uc5d0 \uc0c8\ub85c\uc6b4 \ubcc0\uc218\ub97c \uc0dd\uc131\ud569\ub2c8\ub2e4.\\r\\n`window` \uac1d\uccb4\ub97c \ucc38\uc870\ud558\uc5ec GC\ub97c \ud1b5\ud55c \uba54\ubaa8\ub9ac\uac00 \uc815\ub9ac\ub418\uc9c0 \uc54a\uc544 \uaddc\ubaa8\uac00 \ud06c\ub2e4\uba74 \uc870\uc2ec\ud574\uc57c \ud569\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\nfunction foo(arg) {\\r\\n  name = \\"minsgy\\"; // window.name\\r\\n}\\r\\n```\\r\\n\\r\\n#### setInterval, setTimeout, callback\\r\\n\\r\\n```js\\r\\n// 1\ubc88\uc9f8. Observer Time API\\r\\n// \ucc38\uc870\ud55c Node\ub098 \ub370\uc774\ud130\uac00 \ub354 \ud544\uc694\ub85c \ud558\uc9c0\uc54a\ub294 timer\ub97c \uc0ac\uc6a9\ud55c \uacb0\uacfc\ub97c \ubcf4\uc5ec\uc90d\ub2c8\ub2e4.\\r\\nlet serverData;\\r\\n\\r\\nsetInterval(function () {\\r\\n  let renderer = document.getElementById(\\"renderer\\");\\r\\n  if (renderer) {\\r\\n    renderer.innerHTML = JSON.stringify(serverData);\\r\\n  }\\r\\n}, 5000); // \ub9e4 5\ucd08 \ub9c8\ub2e4 \uc2e4\ud589\\r\\n```\\r\\n\\r\\n\uc704 \ucf54\ub4dc\ub97c \ud1b5\ud574 renderer \uac1d\uccb4\ub294 \uc5b4\ub290 \uc2dc\uc810\uc5d0 \ub2e4\ub978 \uac83\uc73c\ub85c \ub300\uccb4\ub418\uac70\ub098 \uc81c\uac70\ud560 \uc218 \uc788\uc73c\uba70 `Interval`\ub85c \uc313\uc778 \ucf54\ub4dc\ub294 \ud544\uc694\uac00 \uc5c6\uac8c \ub429\ub2c8\ub2e4. \uadf8\ub7ec\ub098 `Interval`\uc740 \ud65c\uc131\ud654\ub41c \uc0c1\ud0dc\ub85c GC\ub97c \ud1b5\ud55c \uba54\ubaa8\ub9ac\uac00 \uc815\ub9ac\ub418\uc9c0 \uc54a\uac8c \ub429\ub2c8\ub2e4. \ucd94\uac00\uc801\uc73c\ub85c `serverData` \ub370\uc774\ud130\ub3c4 \ubc18\ud658\ub418\uc9c0 \uc54a\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574\uc11c `clearInterval`\ub97c \ud1b5\ud574 \ub370\uc774\ud130\ub97c \ubc18\ud658\ud574\uc57c \ud569\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// 2\ubc88\uc9f8. Observer Handler \uc81c\uac70\ud558\uae30\\r\\nconst element = ...\\r\\nconst onClick = () => {...}\\r\\nelement.addEventListener(\'click\', onClick) // event \ub4f1\ub85d\\r\\nelement.removeEventListener(\'click\', onClick) // event \uc81c\uac70\\r\\n```\\r\\n\\r\\n\ud604\ub300 \ube0c\ub77c\uc6b0\uc800\uc5d0\uc11c\ub294 `removeEvent`\ub97c \ud638\ucd9c\ud558\uc9c0 \uc54a\uc544\ub3c4 \uc21c\ud658\ucc38\uc870\ub97c \ud0d0\uc9c0\ud558\uc5ec GC\uc5d0\uc11c \uc790\ub3d9 \ucc98\ub9ac\ud558\uc9c0\ub9cc \uad6c\ud615 \ube0c\ub77c\uc6b0\uc800\uc5d0\uc11c \ub3d9\uc791\ud560 \ub54c\ub3c4 \uba54\ubaa8\ub9ac \ub204\uc218\uac00 \uc5c6\ub3c4\ub85d \uc2e0\uacbd\uc368\uc918\uc57c \ud569\ub2c8\ub2e4..\\r\\n\\r\\n#### \uc774 \uc678\uc5d0\ub3c4...\\r\\n\\r\\n**\ud074\ub85c\uc838\ub85c \uc778\ud55c \uba54\ubaa8\ub9ac \ub204\uc218(\uc911\uc694)**, **Internal Node\ub85c \uc778\ud55c DOM \ucc38\uc870**\uc5d0 \ub300\ud55c \ubb38\uc81c\uac00 \uc788\uc2b5\ub2c8\ub2e4. (\ucd94\ud6c4 \uc5c5\ub370\uc774\ud2b8 \uc608\uc815)\\r\\n\\r\\n## React \uc21c\ud658 \ucc38\uc870 \ubb38\uc81c\\r\\n\\r\\nReact\uc5d0\uc11c \uc5ec\ub7ec \ucef4\ud3ec\ub10c\ud2b8\ub4e4\uc744 \ubaa8\ub4c8\ud654\ud558\uba74\uc11c \ubc1c\uc0dd\ud560 \uc218 \uc788\ub294 **\ubaa8\ub4c8 \uc758\uc874\uc131 \ubb38\uc81c**\uc785\ub2c8\ub2e4.\\r\\n\\r\\n\ucf54\ub4dc\ub97c \ud30c\uc77c\ub85c \ubd84\ub9ac\ud558\uc5ec \uc774\uac83\uc744 \ub2e4\ub978 \ud30c\uc77c\uc774 \ubd88\ub7ec\uc640 \uc0ac\uc6a9\ud558\uae30 \uc704\ud574\uc11c \uc774\ub7f0 \uc2dd\uc73c\ub85c **ES6\uc758 \ubaa8\ub4c8 \uc2dc\uc2a4\ud15c\uc744 \ud65c\uc6a9\ud574 \ucef4\ud3ec\ub10c\ud2b8 \ub2e8\uc704 \uac1c\ubc1c**\uc744 \ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// A -> B -> C -> A \uc21c\ud658 \ucc38\uc870 \ubc1c\uc0dd\\r\\n// Uncaught ReferenceError: Cannot access \'A\' before initialization\\r\\n// A.js\\r\\nimport B from \'./B.js\'\\r\\nexport const A = {\\r\\n\\tB()\\r\\n\\t...\\r\\n}\\r\\n\\r\\n// B.js\\r\\nimport C from \'./A.js\'\\r\\nexport const B = {\\r\\n\\tC()\\r\\n\\t...\\r\\n}\\r\\n\\r\\n// C.js\\r\\nimport A from \'./B.js\'\\r\\nexport const C = {\\r\\n\\tA()\\r\\n\\t...\\r\\n}\\r\\n```\\r\\n\\r\\n\ubb38\uc81c\ub294 \ubaa8\ub4c8 \uac04\uc758 \uc11c\ub85c \ucc38\uc870\ud558\ub294 \uacbd\uc6b0 \ucd08\uae30\ud654 \uc21c\uc11c\uc5d0 \uc758\ud574 \uc21c\ud658 \ucc38\uc870\uac00 \ubc1c\uc0dd\ud558\uac8c \ub429\ub2c8\ub2e4. Webpack\uc5d0\uc11c \ubaa8\ub4c8\uc744 \ucc98\ub9ac\ud558\ub294 \ubc29\uc2dd\uc740 \uc758\uc874\uc131 \ub9e8 \ub9c8\uc9c0\ub9c9 \uc21c\uc11c\uc5d0 \uc788\ub294 \ubaa8\ub4c8\ubd80\ud130 \ucd08\uae30\ud654\ud558\uac8c \ub418\uc5b4 \uc774\ub7ec\ud55c \uc21c\ud658 \ucc38\uc870 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud558\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n**\uc989, `B.js` \ud30c\uc77c\uc758 \ucf54\ub4dc\uac00 \ub9e8 \uba3c\uc800 \uc2e4\ud589\ub418\uba74\uc11c \ucd08\uae30\ud654\uac00 \ub418\uc9c0\ub3c4 \uc54a\uc740 `A.js`\ub97c \ucc38\uc870\ud558\uba74\uc11c \ubc1c\uc0dd\ud558\ub294 \ubb38\uc81c\uc785\ub2c8\ub2e4.** \uacb0\uad6d\uc5d0\ub294 \uc21c\ud658\ucc38\uc870\ub77c\ub294 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574\uc11c \uace0\ubbfc\ud574\uc57c \ud569\ub2c8\ub2e4.\\r\\n\\r\\n### \ud574\uacb0 \ubc29\ubc95\\r\\n\\r\\n\uac00\uc7a5 \uac04\ub2e8\ud55c \uac74 \uc6d0\uc778\uc778 \uc21c\ud658\ucc38\uc870\ub97c \uc81c\uac70\ud558\uae30 \uc704\ud55c \ubc29\ubc95\uc73c\ub85c React\uac00 `Top-down` \ubc29\uc2dd\uc758 \ucef4\ud3ec\ub10c\ud2b8 \ud750\ub984\uc744 \uac00\uc9c4\ub2e4\uba74 \ud574\uacb0\uacfc \ub3d9\uc2dc\uc5d0 \uc608\ubc29\uae4c\uc9c0 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uc804\ubc18\uc801\uc778 \ub370\uc774\ud130\uc758 \ud750\ub984\uc744 Atomic Component \ubc29\uc2dd\uc73c\ub85c \uad6c\uc131\ud558\uc5ec \uc81c\uacf5\ud558\ub358\uc9c0 UI \ucef4\ud3ec\ub10c\ud2b8\uc640 Service \ucef4\ud3ec\ub10c\ud2b8\ub97c \uad6c\ubd84\ud558\uc5ec API \ud638\ucd9c\uc744 \ud558\ub294 \ubc29\uc2dd\ub3c4 \uc788\ub294 \ub9cc\ud07c \uc5ec\ub7ec \uc874\uc7ac\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\ud504\ub85c\uc81d\ud2b8\ub97c \uc9c4\ud589\ud558\uba74\uc11c \uc21c\ud658\ucc38\uc870\uac00 \ubb38\uc81c\ub97c \uaf2d \uc77c\uc73c\ud0a4\uc9c0 \uc54a\uc744 \uc218 \uc788\uc9c0\ub9cc \ucd94\ud6c4 \ud504\ub85c\uc81d\ud2b8\uac00 \ucee4\uc9c0\uba74\uc11c \ubc1c\uc0dd\ud560 \uc218 \uc788\ub294 \uc774\uc288\ub4e4\uc744 \uc608\ubc29\ud558\uae30 \uc704\ud55c \ubc29\ud5a5\uc73c\ub85c \uc778\uc9c0\ud558\ub294 \uac8c \uc720\uc9c0\ubcf4\uc218\uc5d0 \uc788\uc5b4\uc11c\ub3c4 \uc88b\uc544\ubcf4\uc785\ub2c8\ub2e4.\\r\\n\\r\\n## Question List\\r\\n\\r\\n1. React\uc5d0\uc11c \uc21c\ud658 \ucc38\uc870 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud558\ub294 \uc774\uc720\ub294 \ubb34\uc5c7\uc778\uac00\uc694?\\r\\n2. \uc2f1\uae00 \uc2a4\ub808\ub4dc\uc778 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uac00 \ub3d9\uc2dc\uc5d0 \uc5ec\ub7ec \uac1c\uc758 \uc791\uc5c5\uc744 \ucc98\ub9ac\ud560 \uc218 \uc788\ub294 \ubc29\ubc95\uc740 \ubb34\uc5c7\uc778\uac00\uc694? (\ube44\ub3d9\uae30\ub97c \ucc98\ub9ac\ud558\ub294 \ubc29\ubc95\uc740?)\\r\\n\\r\\n## Reference\\r\\n\\r\\n[JavaScript \uc5d4\uc9c4\uc5d0 \ub300\ud558\uc5ec](https://engineering.huiseoul.com/%EC%9E%90%EB%B0%94%EC%8A%A4%ED%81%AC%EB%A6%BD%ED%8A%B8%EB%8A%94-%EC%96%B4%EB%96%BB%EA%B2%8C-%EC%9E%91%EB%8F%99%ED%95%98%EB%8A%94%EA%B0%80-v8-%EC%97%94%EC%A7%84%EC%9D%98-%EB%82%B4%EB%B6%80-%EC%B5%9C%EC%A0%81%ED%99%94%EB%90%9C-%EC%BD%94%EB%93%9C%EB%A5%BC-%EC%9E%91%EC%84%B1%EC%9D%84-%EC%9C%84%ED%95%9C-%EB%8B%A4%EC%84%AF-%EA%B0%80%EC%A7%80-%ED%8C%81-6c6f9832c1d9)  \\r\\n[JavaScript\ub294 \uc5b4\ub5bb\uac8c \uc791\ub3d9\ud558\ub294\uac00?](https://engineering.huiseoul.com/%EC%9E%90%EB%B0%94%EC%8A%A4%ED%81%AC%EB%A6%BD%ED%8A%B8%EB%8A%94-%EC%96%B4%EB%96%BB%EA%B2%8C-%EC%9E%91%EB%8F%99%ED%95%98%EB%8A%94%EA%B0%80-%EB%A9%94%EB%AA%A8%EB%A6%AC-%EA%B4%80%EB%A6%AC-4%EA%B0%80%EC%A7%80-%ED%9D%94%ED%95%9C-%EB%A9%94%EB%AA%A8%EB%A6%AC-%EB%88%84%EC%88%98-%EB%8C%80%EC%B2%98%EB%B2%95-5b0d217d788d)  \\r\\n[\ubd88\ubcc0 \uac1d\uccb4](https://ui.toast.com/posts/ko_20220217)"},{"id":"JavaScript Runtime\uc5d0 \ub300\ud558\uc5ec","metadata":{"permalink":"/blog/JavaScript Runtime\uc5d0 \ub300\ud558\uc5ec","editUrl":"https://github.com/minsgy/minsgy.github.io/edit/main/blog/javascript-runtime.md","source":"@site/blog/javascript-runtime.md","title":"JavaScript Runtime\uc5d0 \ub300\ud558\uc5ec","description":"\uc11c\ub860","date":"2022-09-15T01:11:06.000Z","formattedDate":"2022\ub144 9\uc6d4 15\uc77c","tags":[{"label":"JavaScript","permalink":"/blog/tags/java-script"},{"label":"DeepDive","permalink":"/blog/tags/deep-dive"},{"label":"Study","permalink":"/blog/tags/study"}],"readingTime":11.125,"hasTruncateMarker":false,"authors":[{"name":"Choi Minseok","title":"\uc3d8\uce74(SOCAR)\uc5d0\uc11c \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc744 \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.","url":"https://github.com/minsgy","imageURL":"https://github.com/minsgy.png","key":"minsgy"}],"frontMatter":{"slug":"JavaScript Runtime\uc5d0 \ub300\ud558\uc5ec","title":"JavaScript Runtime\uc5d0 \ub300\ud558\uc5ec","authors":["minsgy"],"tags":["JavaScript","DeepDive","Study"]},"prevItem":{"title":"JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30","permalink":"/blog/JavaScript \uba54\ubaa8\ub9ac \uad00\ub9ac\ud558\uae30"}},"content":"## \uc11c\ub860\\r\\n\\r\\n\uc800\ub294 \ud504\ub860\ud2b8\uc5d4\ub4dc \uc8fc\ub2c8\uc5b4 \uac1c\ubc1c\uc790\ub85c \ud65c\ub3d9\ud558\uba70 **JavaScript**\uc5d0 \ub300\ud55c \uc911\uc694\uc131\uc744 \ub9e4\ubc88 \ub290\uaf08\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n**JavaScript**\uc5d0\uc11c \ub370\uc774\ud130\ub97c \uad00\ub9ac\ud558\ub294 \ubc29\ubc95, \uac1d\uccb4 \uc9c0\ud5a5\uc744 \uace0\ub824\ud560 \uc218 \uc788\ub294 \ubc29\ubc95 \ub4f1\\r\\n\uae30\ubcf8\uc801\uc778 \uc6d0\ub9ac\uac00 \ub4b7\ubc1b\uce68\ub418\uc5b4 \uc124\uacc4\ub97c \ud574\uc57c \uc5ec\ub7ec \uc774\uc288 \uc0ac\ud56d\uc5d0\ub3c4 \ub300\uc751\ud560 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\ub610\ud55c, \uc6f9\uc774\ub77c\ub294 \ubd84\uc57c\uc5d0\uc11c \ube60\ub974\uac8c \ubcc0\ud654\ud558\ub294 \uc0c1\ud669\uc5d0\uc11c **React**\uc640 \uac19\uc774 \uc5b4\ub5a0\ud55c \ub77c\uc774\ube0c\ub7ec\ub9ac/\ud504\ub808\uc784\uc6cc\ud06c\uac00 \uc720\ud589\ud560 \uc9c0\ub294 \ubaa8\ub974\uae30 \ub54c\ubb38\uc5d0 \uae30\ucd08\uac00 \ub418\ub294 JavaScript\uc5d0 \ub300\ud55c \uc774\ud574\uac00 \ud544\uc694\ud558\ub2e4 \uc0dd\uac01\ud574 \ub7f0\ud0c0\uc784\uae4c\uc9c0 \ubd84\uc11d\ud574\ubcf4\uc558\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uc774 \ud3ec\uc2a4\ud2b8\ub97c \ud1b5\ud574\uc11c \ubc30\uacbd \uc9c0\uc2dd\uc774 \uad81\uae08\ud55c **JavaScript \uc0ac\uc6a9\ud574\uc624\uc2e0 \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790**\ubd84\ub4e4\uaed8 \ub3c4\uc6c0 \ub418\uae30\ub97c \ubc14\ub78d\ub2c8\ub2e4.\\r\\n\\r\\n## JavaScript\\r\\n\\r\\n> \uc6f9 \ud398\uc774\uc9c0 (HTML/CSS)\uc758 \ubcf4\uc870 \uae30\ub2a5\uc744 \uc218\ud589\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9 \ub41c \uc5b8\uc5b4\uc785\ub2c8\ub2e4.\\r\\n> \uc9c0\uc18d\ub41c \ubc1c\uc804\uc73c\ub85c \ube0c\ub77c\uc6b0\uc800 \uc774\uc678\uc758 \ud658\uacbd\uc5d0\uc11c\ub3c4 \ub3d9\uc791\uc2dc\ud0ac \uc218 \uc788\ub294 \ud658\uacbd\uc774 \uc0dd\uae30\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### JavaScript \ub4f1\uc7a5\uacfc \ud45c\uc900\ud654\\r\\n\\r\\n\ucc98\uc74c JavaScript\ub294 \ub3d9\uc801\uc778 \ud398\uc774\uc9c0 \ub80c\ub354\ub9c1\ub3c4 \uc544\ub2c8\ub77c \ub2e8\uc21c\ud788 \uc11c\ubc84\ub85c\ubd80\ud130 HTML, CSS\ub97c \ub80c\ub354\ub9c1\ud558\ub294 \uc218\uc900\uc73c\ub85c\ub9cc \ud55c\uc815\uc801\uc73c\ub85c \uc0ac\uc6a9\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ud6c4 JavaScript\uc758 \ud575\uc2ec \uc6d0\ub9ac\uc778 \ube44\ub3d9\uae30(Asynchronous) \uac1c\ub150\uc744 \ub2f4\uc740 **Ajax(Asynchronous JavaScript and XML)**\uac00 \ub4f1\uc7a5\ud558\uac8c \ub418\uba74\uc11c \uc6b0\ub9ac\uac00 \uc54c\uace0 \uc788\ub294 \uae30\ub2a5\uc744 \ud65c\uc6a9\ud558\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uae30\uc874 \ub3d9\uae30\uc801 \ubc29\uc2dd\uc73c\ub85c HTML \uc804\uccb4\ub97c \ub2e4\uc6b4\ubc1b\uc544 \ud654\uba74 \uc804\ud658\uc744 \ud588\uc9c0\ub9cc \ubc14\ub00c\uc9c0 \uc54a\ub294 \ub370\uc774\ud130\ub97c \ud3ec\ud568\ud574 \ubd88\ud544\uc694\ud55c \ub370\uc774\ud130\uac00 \ud3ec\ud568\ub418\ub294 \uacbd\uc6b0\uac00 \uc0dd\uacbc\uace0 \ud1b5\uc2e0 \ud6a8\uc728\uacfc UX \uba74\uc5d0\uc11c \uc633\uc9c0 \uc54a\uc740 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub7ec\ud55c \ubb38\uc81c\ub294 \ube44\ub3d9\uae30\ub97c \ud1b5\ud574 \ub3d9\uc801\uc778 \uc6f9 \ud398\uc774\uc9c0\ub97c \uad6c\uc131\ud558\uba74\uc11c \ubcc0\ud654 \ub41c \ub370\uc774\ud130\ub9cc \ubc1b\uc544\uc640 \uc21c\uac04\uc801\uc73c\ub85c \uae5c\ube61\uc774\ub294 \ud604\uc0c1\uc774 \uc5c6\uc5b4\uc838 UX\ub85c\ub3c4 \uc88b\uc740 \uacb0\uacfc\ub97c \ub0bc \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \uacb0\uacfc\ub85c \uc6f9 \ube0c\ub77c\uc6b0\uc800\uc5d0\uc11c\ub3c4 \ub370\uc2a4\ud06c\ud0d1 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uacfc \uc720\uc0ac\ud55c \ud37c\ud3ec\uba3c\uc2a4\ub97c \ub0b4\uba70 \uc8fc\ubaa9\ubc1b\uae30 \uc2dc\uc791\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uadf8\ub7ec\ub098 \ud45c\uc900\ud654\ub418\uc9c0 \uc54a\uc740 JavaScript\uac00 \ube0c\ub77c\uc6b0\uc800\uc5d0 \uc801\uc6a9\ub418\uba74\uc11c \ud06c\ub85c\uc2a4 \ube0c\ub77c\uc6b0\uc9d5 \uc774\uc288\uac00 \ubc1c\uc0dd\ud558\uae30 \uc2dc\uc791\ud588\uace0 \ubaa8\ub4e0 \ube0c\ub77c\uc6b0\uc800\uc5d0\uc11c \uc815\uc0c1 \uc791\ub3d9\ud558\ub294 \uc6f9 \ud398\uc774\uc9c0 \uad6c\uc131\uc774 \uc5b4\ub824\uc6e0\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n![](https://i.imgur.com/IXojwIy.png)\\r\\n\\r\\n\uc774\ub97c \ubcf4\uc644\ud558\uae30 \uc704\ud574 `ECMAScript` \uba85\uc73c\ub85c JavaScript \ud45c\uc900\ud654\uac00 \uc9c4\ud589\ub418\uc5b4 \ud604\uc7ac \ub9ce\uc774 \uc0ac\uc6a9\ud558\ub294 `ES6`, `ES5`\uc640 \uac19\uc740 \ubb38\ubc95\ub4e4\uc774 \uc0ac\uc6a9\ub418\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n### V8, Node.js\uc758 \ub4f1\uc7a5\\r\\n\\r\\nAjax\ub97c \ud1b5\ud55c \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\ub85c\uc11c \uac00\ub2a5\uc131\uc744 \ud655\uc778 \ud55c \uad6c\uae00(Google)\uc740 JavaScript\ub85c \uc6f9 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc744 \ube60\ub974\uace0 \uc815\ud655\ud558\uac8c \ub3d9\uc791\ud558\ub294 \uc5d4\uc9c4\uc758 \ud544\uc694\uc131\uc744 \ub290\uaf08\uace0 \uadf8\ub807\uac8c \uc644\uc131 \ub41c \uc624\ud508\uc18c\uc2a4 \uc5d4\uc9c4\uc778 [V8](https://v8.dev/)\uc774 \ub4f1\uc7a5\ud558\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n![](https://i.imgur.com/zB7qzy1.png)\\r\\n\\r\\n\uc774\ub97c \ud1b5\ud574 JavaScript\ub294 \ub370\uc2a4\ud06c\ud0d1 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uacfc \ube44\uc2b7\ud55c \uc131\ub2a5\uacfc \ud37c\ud3ec\uba3c\uc2a4\ub97c \ubcf4\uc5ec\uc8fc\uc5c8\uace0 \uc6f9 \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\ub85c \uc815\ucc29\ub418\uac8c \ub41c \uacc4\uae30\uac00 \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ud6c4 \ub113\uc740 \ud655\uc7a5\uc131\uc5d0 \ub300\ud55c \uac00\ub2a5\uc131\uc744 \ub113\ud600\uc900 V8 \ub7f0\ud0c0\uc784 \ub77c\uc774\ube0c\ub7ec\ub9ac\uc778 `Node.js`\uc758 \ub4f1\uc7a5\uc73c\ub85c \ube0c\ub77c\uc6b0\uc800 \uc774\uc678\uc758 \ud658\uacbd\uc5d0\uc11c\ub3c4 \ub3d9\uc791\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c5\ub9bd\uc2dc\ud0a8 \uc5ed\ud560\uc744 \ud574\uc8fc\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n![](https://i.imgur.com/jRPm1V3.png)\\r\\n\\r\\n\uae30\ubcf8\uc801\uc73c\ub85c \ube44\ub3d9\uae30 I/O\ub97c \uc9c0\uc6d0\ud558\uace0 \uc2f1\uae00 \uc2a4\ub808\ub4dc(Single Thread) \uc774\ubca4\ud2b8 \ub8e8\ud504\ub97c \uae30\ubc18\ud558\uc5ec \ub3d9\uc791\ud558\uace0 \uc2e4\uc2dc\uac04 \ub370\uc774\ud130 \ucc98\ub9ac\uc5d0\ub294 \ud6a8\uacfc\uc801\uc774\uc9c0\ub9cc CPU \uc0ac\uc6a9\ub960\uc774 \ub192\uc740 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc5d0\ub294 \uad8c\uc7a5\ud558\uc9c0 \uc54a\ub294 \ubc29\uc2dd\uc785\ub2c8\ub2e4. \uadf8\ub9ac\uace0 **Node.js\uc758 \uac1c\ubc1c \uc2dc\uc810\uc774 JavaScript \ubaa8\ub4c8\uc758 \ud45c\uc900\uc774 CommonJS\ub85c \uc774\ub8e8\uc5b4\uc838 ES Module**\uc774 \uc0c8\ub85c\uc6b4 \ud45c\uc900\uc774 \ub41c \ud604\uc7ac\ub85c\uc11c\ub294 \ubd80\uc871\ud55c \ubd80\ubd84\uc785\ub2c8\ub2e4. \uadf8\ub9ac\uace0 TypeScript\uac00 \ud544\uc218\ubd88\uac00\uacb0\ub85c \uc0ac\uc6a9\ud558\ub294 \uc0c1\ud669\uc5d0 \uc9c0\uc6d0\ud558\uc9c0 \uc54a\ub294 \ub2e8\uc810\ub3c4 \uc874\uc7ac\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub7ec\ud55c Node.js\uc758 \ub2e8\uc810\uc744 \ubcf4\uc644\ud558\uace0 \ubc1c\uc804 \ub41c \ub7f0\ud0c0\uc784(Runtime) \ub77c\uc774\ube0c\ub7ec\ub9ac\uc778 **Deno**\ub97c \uc18c\uac1c\ud569\ub2c8\ub2e4.\\r\\n\\r\\n### Deno\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/190108920-7897ed40-5b4a-481d-a1e0-d5a1e3db12bc.png)\\r\\n\\r\\nDeno\uac00 \ub4f1\uc7a5\ud558\uac8c \ub41c \uac74 2020\ub144 5\uc6d4\uc774\uba70, Node.js\uc758 \ub2e8\uc810\uc744 \ubcf4\uc644\ud558\uace0\uc790 TypeScript \uc9c0\uc6d0, ES Module \uc9c0\uc6d0, \ube0c\ub77c\uc6b0\uc800\uc640 Node.js\uc758 \ud638\ud658\uc131, \ubcf4\uc548 \ubb38\uc81c, \ube60\ub978 \uc2e4\ud589 \uc18d\ub3c4 \uac1c\uc120\uacfc \uba85\uc2dc\uc801\uc778 \ud328\ud0a4\uc9c0 \uad00\ub9ac\ub97c \uac1c\uc120\ud558\uba70 \ub4f1\uc7a5\ud558\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n#### Promise\\r\\n\\r\\nNode.js\uc758 \ub0b4\uc7a5\ub41c \uc5ec\ub7ec \ube44\ub3d9\uae30 \ub85c\uc9c1\uc740 Promise API\ub97c \ud65c\uc6a9\ud558\uc9c0 \uc54a\uace0 \uc608\uc804 \ubc29\uc2dd\uc758 Callback \ud328\ud134\uc744 \uc791\uc131\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub85c \uc778\ud574 node async api\uac00 \ub178\ud6c4\ud654\ub418\uc5b4 \uc544\uc26c\uc6b4 \ud37c\ud3ec\uba3c\uc2a4\ub97c \ubcf4\uc5ec\uc8fc\uac8c \ub429\ub2c8\ub2e4.\\r\\n\\r\\n\uadf8\ub807\uc9c0\ub9cc Deno\uc758 \uacbd\uc6b0 Promise API\ub97c \uae30\ubcf8\uc73c\ub85c \uc0ac\uc6a9\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub85c \uc778\ud574 \ub354\uc6b1 \ube60\ub978 \ud37c\ud3ec\uba3c\uc2a4\ub97c \ubcf4\uc5ec\uc8fc\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// deno - async \uad6c\ubb38\uc774 \uc790\ub3d9\uc73c\ub85c \uc801\uc6a9\ub429\ub2c8\ub2e4.\\r\\nconst data = await fetch(\\"URL\\");\\r\\nconst result = await data.json();\\r\\nconsole.log(result);\\r\\n```\\r\\n\\r\\n#### Packages, Npm\\r\\n\\r\\nLicense, repository, description \ub4f1 \ub2e4\uc591\ud55c \uc815\ubcf4\ub97c \ub2f4\uace0 \uc788\ub294 package.json\uc740 \ubd88\ud544\uc694\ud55c \uc815\ubcf4\uac00 \ub9ce\uc2b5\ub2c8\ub2e4. \uc2e4\uc81c\ub85c\ub294 \ud30c\uc77c\uacfc URL\ub9cc \uc0ac\uc6a9\ud558\uba74 \uc885\uc18d\uc131\uc744 \ub098\uc5f4\ud560 \ud544\uc694\uac00 \uc5c6\uc5b4\uc9d1\ub2c8\ub2e4.\\r\\n\\r\\nDeno\ub294 \uc774\ub7ec\ud55c package.json\uc744 \uc81c\uac70\ud558\uace0 URL\ub9cc\uc73c\ub85c \uc885\uc18d\uc131\uc744 \ub098\uc5f4\ud560 \uc218 \uc788\ub3c4\ub85d \ud558\uc600\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\ub610\ud55c `npm(closed source)` \ub97c \uc0ac\uc6a9\ud558\uc9c0 \uc54a\uc544 \ub3c5\ub9bd\uc131\uc744 \ub192\uc77c \uc218 \uc788\ub2e4\ub294 \uc7a5\uc810\uc774 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\uc774\ub97c \ud1b5\ud574 Github, GitLab, Bitbucket \ub4f1 \ub2e4\uc591\ud55c \ud50c\ub7ab\ud3fc\uc5d0\uc11c \uac00\uc838\uc640 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n```js\\r\\n// node.js\\r\\nconst moment = require(\\"moment\\");\\r\\nconsole.log(moment.version);\\r\\n\\r\\n// deno\\r\\nimport { moment } from \\"https://deno.land/x/deno_moment@1.0.0/moment.ts\\";\\r\\nconsole.log(moment.version);\\r\\n```\\r\\n\\r\\n#### Deno\uac00 node.js\ub97c \ub300\uccb4\ud560 \uc218 \uc788\uc744\uae4c?\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/190184861-0bdbeded-f0e0-45ca-a113-71076e918608.png)\\r\\n\\r\\n\ud604\uc7ac 2022\ub144 9\uc6d4 \uae30\uc900\uc73c\ub85c Deno \uc11c\ube44\uc2a4\ub294 v1.25.2 \ub9b4\ub9ac\uc988 \uc911\uc785\ub2c8\ub2e4.\\r\\n\\r\\n\ucd5c\uadfc\uae4c\uc9c0\ub3c4 \uc5c5\ub370\uc774\ud2b8\ub97c \ud558\ub294 \ub9cc\ud07c \ucc28\uc138\ub300 JavaScript \ub7f0\ud0c0\uc784\uc73c\ub85c \ubc1c\ub3cb\uc6c0 \ud560 \uc218 \uc788\ub3c4\ub85d \uafb8\uc900\ud55c \uac1c\ubc1c\uc5d0 \uc788\uc2b5\ub2c8\ub2e4. \uacfc\uac70 \uc794\uc7ac\uc778 CommonJS \ubaa8\ub4c8\uc744 \ubcc0\uacbd\ud558\uace0 \uc0ac\uc6a9\uc131\uc774 \ub9ce\uc740 TypeScript\ub97c \uc81c\uacf5\ud558\ub294 \ub9cc\ud07c \uc55e\uc73c\ub85c\uc758 \uae30\ub300\uac00 \uc788\ub294 \ub7f0\ud0c0\uc784 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub77c\uace0 \uae30\ub300\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uadf8\ub7ec\ub098 \ud604\uc7ac Deno\uc5d0 \uc0c1\uc751\ud558\ub294 TypeScript package \uc9c0\uc6d0\uc774 \uc544\uc26c\uc6b4\ub9cc\ud07c \uc0ac\uc6a9\uc131\uc5d0 \uc788\uc5b4\uc11c node.js\uac00 \uc555\ub3c4\uc801\uc778 \uc2dc\uc7a5\uc744 \ucc28\uc9c0\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc810\uc810 TypeScript \ud504\ub85c\uc81d\ud2b8 \uc2dc\uc7a5\uc774 \ub113\uc5b4\uc9c4\ub2e4\uba74 \ucda9\ubd84\ud788 \uc0ac\uc6a9\ud560\ub9cc\ud55c \ub9e4\ub825\uc801\uc778 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ud6c4 \ub354 \ube60\ub978 \uc18d\ub3c4\ub97c \uc704\ud574 \ud0c4\uc0dd\ud55c \ub7f0\ud0c0\uc784 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc18c\uac1c\ud569\ub2c8\ub2e4.\\r\\n\\r\\n### Bun\uc758 \ub4f1\uc7a5\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/190210190-c14a338f-86f7-4b84-9398-96219b269dc2.png)\\r\\n\\r\\n\uc0c8\ub86d\uac8c JavaScript \ub7f0\ud0c0\uc784 \ubc0f \ud328\ud0a4\uc9c0 \uad00\ub9ac\uc790\uc778 Bun\uc774 \ub4f1\uc7a5\ud588\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uae30\uc874 V8 \uc5d4\uc9c4\uc744 \ud65c\uc6a9\ud558\ub358 **Node, Deno**\uc640 \ube44\uad50\ub418\ub294 **JavaScriptCore** \uae30\ubc18\uc73c\ub85c \uad6c\ucd95\ub418\uc5b4 \ube60\ub974\uac8c \ub3d9\uc791\ud569\ub2c8\ub2e4. \uc800\uc218\uc900 \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\uc778 Zig\ub85c \uc791\uc131\ub418\uc5b4\uc11c \uac00\ub2a5\ud55c \uc131\ub2a5\uc785\ub2c8\ub2e4.\\r\\n\\r\\n![image](https://user-images.githubusercontent.com/60251579/190211078-d8516115-f153-4c63-b944-14b5fb3e4838.png)\\r\\n\\r\\n\ud2b9\uc9d5\uc73c\ub85c\ub294 node.js \ubaa8\ub4c8\uc5d0\uc11c \uc0ac\uc6a9\ud558\ub294 node_modules\ub97c \uadf8\ub300\ub85c \uc0ac\uc6a9\ud558\uba70 npm\uacfc \ud638\ud658\ud560 \uc218 \uc788\ub294 \ud328\ud0a4\uc9c0 \ub9e4\ub2c8\uc800\ub97c \ud3ec\ud568\ud558\uac8c \ub429\ub2c8\ub2e4. \uadf8\ub798\uc11c node.js\uc758 \uc0ac\uc6a9\ubc95\uacfc \uc720\uc0ac\ud569\ub2c8\ub2e4. \ub610\ud55c commonJS\uc640 ESM \ub458 \ub2e4 \uc9c0\uc6d0\ud558\uba70 JavaScript/TypeScript\ub97c \uc9c0\uc6d0\ud558\uc5ec \uac1c\ubc1c \ud658\uacbd\uc5d0 \ub300\ud55c \ud655\uc7a5\uc131\uc774 \ub113\uc2b5\ub2c8\ub2e4.\\r\\n\\r\\n\uc774\ub807\uac8c \ubcf8\ub2e4\uba74 Demo\uc640 Node.js\uac00 \uac00\uc9c4 \ubb38\uc81c\uc810\uc744 \ubcf4\uc644\ud558\uace0 \uc788\uc9c0\ub9cc, \uc544\uc9c1 v1\ub3c4 \ub098\uc624\uc9c0 \uc54a\uc740 beta version\uc778 \uc0c1\ud0dc\uc785\ub2c8\ub2e4. \ud604\uc7ac \ub7f0\ud0c0\uc784 \uc18d\ub3c4\ub9cc \ubc14\ub77c\ubcf8\ub2e4\uba74 \uad1c\ucc2e\uc9c0\ub9cc \ubcf4\uc548 \ubc0f \ub2e4\ub978 \uc774\uc288\uc5d0 \ub300\ud574 \uc5b4\ub5bb\uac8c \ub300\uc751\ud560 \uc9c0 production \ud658\uacbd\uc5d0\uc11c \uc801\uc6a9\ud558\uae30\uc5d0\ub294 \uc2dc\uac04\uc774 \ud544\uc694\ud574\ubcf4\uc785\ub2c8\ub2e4.\\r\\n\\r\\n## Question List\\r\\n\\r\\n1. \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\ub294 \ubb34\uc2a8 \uc5b8\uc5b4\uc778\uac00? (\uc5b8\uc5b4\uc758 \ud2b9\uc9d5, \ubcf8\uc778\uc774 \uc0dd\uac01\ud558\ub294 \uc790\ubc14\uc2a4\ud06c\ub9bd\ud2b8\uc758 \ud2b9\uc9d5\ub3c4 \uc88b\uc74c)\\r\\n2. \uc778\ud130\ud504\ub9ac\ud130 \uc5b8\uc5b4\uc640 \ucef4\ud30c\uc77c \uc5b8\uc5b4\uc5d0 \ub300\ud574 \ub300\ub2f5\ud558\uace0 \ucc28\uc774\uc810\uc740 \ubb34\uc5c7\uc778\uac00\uc694?\\r\\n3. Optional Chaining\uacfc Non-null assertion operator\uc5d0 \ub300\ud574 \uc544\ub294 \ub300\ub85c \uc124\uba85\ud574\uc8fc\uc138\uc694.\\r\\n4. !!, ??, && \uc5f0\uc0b0\uc790\uc5d0 \ub300\ud574 \uc124\uba85\ud574\uc8fc\uc138\uc694.\\r\\n5. \uba54\ubaa8\ub9ac \ud560\ub2f9\uacfc \uba54\ubaa8\ub9ac \ucc38\uc870, \uac00\ube44\uc9c0 \ucf5c\ub809\ud130\uc5d0 \ub300\ud574 \uc544\ub294 \ub300\ub85c \uc124\uba85\ud574\uc8fc\uc138\uc694. \uc6d0\uc2dc\ud0c0\uc785\uacfc \uac1d\uccb4\ud0c0\uc785\uc744 \uac00\uc9c0\uace0 \uc124\uba85\ud574\uc8fc\uc138\uc694.\\r\\n6. var, let, const\uc5d0 \ub300\ud574 \uc544\ub294 \ub300\ub85c \uc124\uba85\ud574\uc8fc\uc138\uc694.\\r\\n7. \ud638\uc774\uc2a4\ud305\uc774 \ubb34\uc5c7\uc774\uace0 \uc77c\uc5b4\ub098\ub294 \uacfc\uc815\uc5d0 \ub300\ud574 \uc544\ub294 \ub300\ub85c \uc124\uba85\ud574\uc8fc\uc138\uc694.\\r\\n8. `if (!data)`\uc640 `if (data === undefined)`\uc758 \ucc28\uc774\uc810\uc740 \ubb34\uc5c7\uc778\uac00\uc694?\\r\\n\\r\\n## Reference\\r\\n\\r\\n[deno](https://deno.land/)\\r\\n[bun](https://bun.sh/)  \\r\\n[deno vs node.js \ube44\uad50\ud558\uae30](https://www.youtube.com/watch?v=Uc03tAqC6fQ)  \\r\\n[\uc5ec\uae30\uc5b4\ub54c Tech Blog](https://techblog.gccompany.co.kr/%ED%95%9Cbun-%EC%8D%A8%EB%B3%B4%EB%8A%94-%EA%B1%B0-%EC%96%B4%EB%95%8C-fa3cb32ac76f)"}]}')}}]);